import React, { useEffect, useState } from "react";
import { Modal, Button, Tabs, Row, Col, Table, Image, Form, Input, Upload, Dropdown, Space, Select } from "antd"
import { DownOutlined } from '@ant-design/icons';
import styled from 'styled-components';
import moment from "moment";
import { addTourChiTiet, updateTourChiTiet } from "./StaffAcceptedService";
import { toast } from "react-toastify";
import { getTour } from "../create-staff/CreateStaffService"

const { Option } = Select;

toast.configure({
    autoClose: 1000,
    draggable: false,
    limit: 3,
    closeOnClick: true
});

const beforeUpload = (file) => {
    const isJpgOrPng = file.type === "image/jpeg" || file.type === "image/png" || file.type === "image/jpg";
    if (!isJpgOrPng) {
        alert("You can only upload JPG/PNG file!");
    }
    return isJpgOrPng;
}

function StaffAcceptedDialog(props) {

    const { open, onCancel, item } = props
    const [form] = Form.useForm()
    const [timeItem, setTimeItem] = useState()

    const [listTour, setListTour] = useState([])

    const [fileImage, setFileImage] = useState()
    const handleUpload = (event) => {
        console.log('Tên tệp tin:', event.file.name);
        setFileImage(event.file.name)
    }

    const submitForm = async (values) => {

        const newValue = {
            ...values,
            hinhAnh: fileImage
        }


        if (item) {
            const res = await updateTourChiTiet(item.idChiTiet, newValue)
            if (res.status === 200) {
                toast.success("Sửa Thành công")
            } else {
                toast.error("Loi roi !!!!!!!!!!!!!!")
            }
        } else {
            const res = await addTourChiTiet(newValue)
            if (res.status === 200) {
                toast.success("Thêm thành công")
            } else {
                toast.error("Loi roi !!!!!!!!!!!!!!")
            }
        }
        form.resetFields()
        onCancel()
    }

    useEffect(() => {
        if (item) {
            const newItem = {
                ...item,
                ngayDi: moment(item.ngayDi, "YYYY-MM-DD").format("YYYY-MM-DD"),
                ngayVe: moment(item.ngayVe, "YYYY-MM-DD").format("YYYY-MM-DD")
            }
            if (newItem) {
                form.setFieldsValue(newItem)
            }
        }
    }, [])

    useEffect(() => {
        const getListTour = async () => {
            const res = await getTour()
            // console.log("==>", res.data)
            const data = res.data
            const newData = data.map(item => {
                return {

                    id: item.id,
                    tenTour: item.tenTour

                }
            })
            // console.log("==>", newData)
            setListTour(newData)
        }

        getListTour()
    }, [])



    return (
        <Modal
            title="Them Tour"
            open={open}
            width={1000}
            style={{ height: 700 }}
            // onOk={handleOk} 
            onCancel={onCancel}
            footer={null}
        >
            <Form
                form={form}
                onFinish={submitForm}
                layout="vertical"
            >
                <Row gutter={[16, 16]}>
                    <Col span={6}>
                        <Form.Item
                            name="idTour"
                            label="Id"
                            rules={[
                                {
                                    required: true,
                                    message: "Bat buoc phai nhap"
                                },
                                {
                                    pattern: /^[1-9]\d*$/,
                                    message: "ID > 0"
                                }
                            ]}
                        >
                            <Input type="number" />
                        </Form.Item>
                    </Col>

                    <Col span={6}>
                        <Form.Item
                            name="ngayDi"
                            label="Ngay di"
                            rules={[
                                {
                                    required: true,
                                    message: "Bat buoc phai nhap"
                                },
                            ]}
                        >
                            <Input type="date" style={{ lineHeight: "21px" }} />
                        </Form.Item>
                    </Col>

                    <Col span={6}>
                        <Form.Item
                            name="ngayVe"
                            label="Ngay ve"
                            rules={[
                                {
                                    required: true,
                                    message: "Bat buoc phai nhap"
                                },
                            ]}
                        >
                            <Input type="date" style={{ lineHeight: "21px" }} />
                        </Form.Item>
                    </Col>

                    <Col span={6}>
                        <Form.Item
                            name="soLuongCon"
                            label="So luong con"
                            rules={[
                                {
                                    required: true,
                                    message: "Bat buoc phai nhap"
                                },
                                {
                                    pattern: /^[0-9]\d*$/,
                                    message: "So luong >= 0"
                                }
                            ]}
                        >
                            <Input type="number" />
                        </Form.Item>
                    </Col>
                </Row>

                <Row gutter={[16, 16]}>
                    <Col span={12}>
                        <Form.Item
                            name="ghiChu"
                            label="Ghi chu"
                            rules={[
                                {
                                    required: true,
                                    message: "Bat buoc phai nhap"
                                }
                            ]}
                        >
                            <Input />
                        </Form.Item>
                    </Col>

                    <Col span={6}>
                        <Form.Item
                            name="idTour"
                            label="Id tour"
                            rules={[
                                {
                                    required: true,
                                    message: "Bat buoc phai nhap"
                                }
                            ]}
                        >
                            <Select>
                                {
                                    listTour && listTour.map(item => {
                                        <Option value={item.id}>{item.tenTour}</Option>
                                    })
                                }

                                {/* <Option value="option2">Option 2</Option>
                                <Option value="option3">Option 3</Option> */}
                            </Select>
                        </Form.Item>
                    </Col>

                </Row>

                <Row>
                    <Col span={8} className="image-info">
                        <Row>
                            <div className="avatar" >
                                <Image
                                    // src={fileImage ? fileImage?.toString() : "error"}
                                    src={fileImage && `http://127.0.0.1:8887/${fileImage}`}
                                    fallback="data:image/png;base64,"
                                />
                                {/* <img src={data?.anhMot && `http://127.0.0.1:8887/${data.anhMot}`} alt="ing" /> */}
                            </div>
                        </Row>
                        <Row >
                            <Form.Item name="hinhAnh">
                                <Upload
                                    name="hinhAnh"
                                    action="https://www.mocky.io/v2/5cc8019d300000980a055e76"
                                    onChange={handleUpload}
                                    beforeUpload={beforeUpload}
                                    showUploadList={false}
                                >
                                    <Button >
                                        Thêm ảnh đại diện
                                    </Button>
                                </Upload>
                            </Form.Item>
                        </Row>
                    </Col>
                </Row>

                <Row justify={"center"}>
                    <Form.Item>
                        <Button type="primary" htmlType="submit">
                            Submit
                        </Button>
                    </Form.Item>
                </Row>
            </Form>
        </Modal>
    )
}

export default StaffAcceptedDialog

/**
 *  {      
        "idTour" : 1
        "ngayDi": "2023-06-06",
        "ngayVe": "2019-06-10",
        "soLuongCon": 23,
        "ghiChu": "dsddsdsdsd",
        "hinhAnh": "YbhTzyNe2EuyMf3uPtC3JGdyR48ApRNX9l9hH5Qk.jpeg",
 }
 */

const StyledModal = styled(Modal)`
    .ant-modal-content{
        overflow: hidden;
        height: 90vh;
    }
    .ant-modal-body{
        min-height: 76vh;
        overflow: hidden;
    }

    .ant-modal-footer{
        position: absolute;
        bottom: 0;

    }

    input[type="date"] {
        line-height: 21px;
    }
`;
