import React, { useState } from 'react';
import { Button, Col, DatePicker, Form, Input, Row, Table } from 'antd';
import { ActionButton } from 'app/views/general-settings/TableButton';
import moment from 'moment';
import RegisterProfileForm from './RegisterProfileForm';

const { TextArea } = Input;

function RegisterCollapse({ formRef, employeeData, staffInfo, onSend, onDelete }) {
    const [registerForm, setRegisterForm] = useState(false);
    const [registerRecord, setRegisterRecord] = useState(false);

    const [form] = Form.useForm();

    const addProfileList = (values) => {
        values.date = values.date.format('YYYY-MM-DD');
        handleShowRegisterForm();
        form.resetFields();
    };

    const onReset = () => {
        form.resetFields();
    };

    const handleShowRegisterForm = () => {
        setRegisterForm(!registerForm);
    };

    return (
        <div className="content-form-layout">
            <Form onFinish={addProfileList} ref={formRef} form={form}>
                <Row>
                    <Col span={24} md={24} xl={8}>
                        <Form.Item
                            name="hoso"
                            label="Hồ sơ"
                            rules={[
                                {
                                    required: true,
                                    message: 'Bắt buộc phải chọn trường này!',
                                },
                                { whitespace: true, message: 'không được để khoảng trằng trống' },
                            ]}
                        >
                            <Input />
                        </Form.Item>
                    </Col>

                    <Col span={24} md={12} xl={5}>
                        <Form.Item
                            name="date"
                            label="Ngày đăng kí"
                            rules={[
                                {
                                    required: true,
                                    message: 'Bắt buộc phải chọn trường này!',
                                },
                            ]}
                        >
                            <DatePicker placeholder="YYYY-MM-DD" format="YYYY-MM-DD" />
                        </Form.Item>
                    </Col>

                    <Col span={24} md={12} xl={11}>
                        <Form.Item
                            name="note"
                            label="Ghi chú"
                            rules={[
                                {
                                    required: true,
                                    message: 'Bắt buộc phải chọn trường này!',
                                },
                                { whitespace: true, message: 'không được để khoảng trằng trống' },
                            ]}
                        >
                            <Input />
                        </Form.Item>
                    </Col>

                    <Col span={24} md={12} xl={13}>
                        <Form.Item
                            name="content"
                            label="Nội dung"
                            rules={[
                                {
                                    required: true,
                                    message: 'Bắt buộc phải chọn trường này!',
                                },
                                { whitespace: true, message: 'không được để khoảng trằng trống' },
                            ]}
                        >
                            <TextArea
                                autoSize={{
                                    minRows: 1,
                                    maxRows: 3,
                                }}
                            />
                        </Form.Item>
                    </Col>

                    <Col
                        span={24}
                        md={24}
                        xl={11}
                        style={{ display: 'flex', marginTop: '10px', alignItems: 'flex-end' }}
                    >
                        <Form.Item>
                            <Button className="add-form" type="primary" htmlType="submit">
                                Thêm
                            </Button>
                            <Button className="cancel-form" htmlType="button" onClick={onReset} type="primary">
                                Hủy
                            </Button>
                        </Form.Item>
                    </Col>
                </Row>
            </Form>

            <Table
                columns={[
                    {
                        key: '1',
                        title: 'Hành động',
                        render: (record) => {
                            return (
                                <ActionButton
                                    update
                                    deleted
                                    item={record}
                                    onSelect={(record, method) => {
                                        if (method === 0) {
                                            setRegisterRecord(record);
                                            handleShowRegisterForm();
                                        } else if (method === 1) {
                                            onDelete();
                                        } else {
                                            alert('Call Selected Here:' + record.id);
                                        }
                                    }}
                                />
                            );
                        },
                    },
                    { key: '2', title: 'Hồ sơ', dataIndex: 'hoso' },
                    {
                        key: '3',
                        title: 'Ngày đăng kí',
                        render: (record) => {
                            return (
                                <div>
                                    <p>{moment(record.date).format('DD-MM-YYYY')}</p>
                                </div>
                            );
                        },
                    },
                    { key: '4', title: 'Nội dung', dataIndex: 'content' },
                    { key: '5', title: 'Ghi chú', dataIndex: 'note' },
                ]}
                rowKey="6"
                dataSource={employeeData}
                pagination={{
                    defaultPageSize: 10,
                    showSizeChanger: true,
                    pageSizeOptions: ['5', '10', '20', '30'],
                }}
                style={{ margin: '15px 5px 0 5px' }}
            />

            {registerForm && (
                <RegisterProfileForm
                    open={registerForm}
                    onSend={onSend}
                    onOk={handleShowRegisterForm}
                    onCancel={handleShowRegisterForm}
                    staffInfo={staffInfo}
                    registerRecord={registerRecord}
                />
            )}
        </div>
    );
}

export default RegisterCollapse;
