import React from 'react';
import { Col, Collapse, Form, Image, Input, Modal, Row, Space, Typography } from 'antd';
import { useState } from 'react';
import { useEffect } from 'react';
import { getStaffById } from '../UpdateHappeningsService';
import RegisterCollapse from '../registerProfile/RegisterCollapse';
import SalaryIncreaseCollapse from '../salaryIncrease/SalaryIncreaseCollapse';
import PromotedCollapse from '../promoted/PromotedCollapse';
import ProposedCollapse from '../proposed/ProposeCollapse';
import ResignationApplicationForm from '../ResignationApplicationForm';
import moment from 'moment';
import DialogSendLeader from '../leadershipSubmissionForm';
import ModalDelete from '../ModalDelete';

const { Panel } = Collapse;

function ModalUpdateHappenings({ open, openUpdateStatus, staffInfo }) {
    const [employeeData, setEmployeeData] = useState([])
    const [formResignationApplication, setFormResignationApplication] = useState(false);
    const [formLeadershipSubmission, setFormLeadershipSubmission] = useState(false);
    const [modalDelete, setModalDelete] = useState(false);

    const handleGetStaffById = async (staffInfo) => {
        const res = await getStaffById({ employeeId: staffInfo?.employeeId });
        setEmployeeData(res?.data?.data)
    };

    useEffect(() => {
        if (staffInfo?.employeeId) {
            handleGetStaffById(staffInfo);
        }
    }, [staffInfo?.employeeId]);

    const handleOpenFormResignationApplication = () => {
        setFormResignationApplication(!formResignationApplication);
    };

    const handleOpenFormLeadershipSubmission = () => {
        setFormLeadershipSubmission(!formLeadershipSubmission);
    };

    const handleOpenModalDelete = () => {
        setModalDelete(!modalDelete);
    };

    return (
        <>
            <Modal
                okText="KẾT THÚC"
                centered
                cancelText="Hủy"
                title="Cập nhật diễn biến"
                open={open}
                onOk={handleOpenFormResignationApplication}
                onCancel={openUpdateStatus}
                className="conclude_modal"
                width={'70vw'}
            >
                <Row justify="space-between" gutter={[16, 16]}>
                    <Col span={8} style={{ marginBottom: '20px' }}>
                        <div>
                            <div className="avatar">
                                <Image src={staffInfo?.photoUrl || `https://zos.alipayobjects.com/rmsportal/jkjgkEfvpUPVyRjUImniVslZfWPnJuuZ.png`} />
                            </div>
                            <div className="upload-image"></div>

                            <div className="name-user">
                                <Typography.Title level={4} style={{ marginBottom: '5px', textTransform: 'uppercase' }}>
                                    {staffInfo.fullName}
                                </Typography.Title>
                                <Typography.Title level={5} style={{ margin: 0 }}>
                                    {staffInfo.teamId === 1 && "Back-End"}
                                    {staffInfo.teamId === 2 && "Front-End"}
                                    {staffInfo.teamId === 3 && "Tester"}
                                </Typography.Title>
                            </div>
                        </div>
                    </Col>
                    <Col span={16} style={{ marginBottom: '20px' }}>
                        <div className="account-information">
                            <div className="top-form">
                                <Typography>Thông tin cơ bản</Typography>
                            </div>
                            <div className="content-form">
                                <Row>
                                    <Col span={7}>
                                        <Form.Item name={['user', 'name']} label="Họ và tên">
                                            <Input defaultValue={staffInfo.fullName} />
                                        </Form.Item>
                                    </Col>

                                    <Col span={5}>
                                        <Form.Item name={['user', 'code']} label="Mã nhân viên">
                                            <Input defaultValue={staffInfo.code} />
                                        </Form.Item>
                                    </Col>

                                    <Col span={6}>
                                        <Form.Item name={['user', 'phone']} label="Số điện thoại">
                                            <Input defaultValue={staffInfo.phone} />
                                        </Form.Item>
                                    </Col>

                                    <Col span={6}>
                                        <Form.Item name="date" label="Ngày sinh">
                                            <Input defaultValue={moment(staffInfo.dateOfBirth).format("DD-MM-YYYY")} />
                                        </Form.Item>
                                    </Col>

                                    <Col span={7}>
                                        <Form.Item name={['user', 'location']} label="Vị trí">
                                            <Input defaultValue={staffInfo.registerPosition} />
                                        </Form.Item>
                                    </Col>

                                    <Col span={17}>
                                        <Form.Item name={['user', 'address']} label="Địa chỉ">
                                            <Input defaultValue={staffInfo.address} />
                                        </Form.Item>
                                    </Col>
                                </Row>
                            </div>
                            <div className="bottom-form"></div>
                        </div>
                    </Col>

                    <Space direction="vertical" style={{ width: '100%' }}>
                        <Collapse expandIconPosition="end">
                            <Panel header="Đăng kí hồ sơ" key="1">
                                <RegisterCollapse employeeData={[
                                    {
                                        id: 1,
                                        hoso: "ho so 1",
                                        date: "20-01-2023",
                                        content: "hom nay la ngay cuoi thang",
                                        note: "chu y day nhanh tien do len"
                                    },
                                    {
                                        id: 2,
                                        hoso: "ho so 2",
                                        date: "20-01-2023",
                                        content: "hom nay la ngay cuoi thang",
                                        note: "chu y day nhanh tien do len"
                                    },
                                    {
                                        id: 3,
                                        hoso: "ho so 3",
                                        date: "20-01-2023",
                                        content: "hom nay la ngay cuoi thang",
                                        note: "chu y day nhanh tien do len"
                                    },
                                    {
                                        id: 4,
                                        hoso: "ho so 4",
                                        date: "20-01-2023",
                                        content: "hom nay la ngay cuoi thang",
                                        note: "chu y day nhanh tien do len"
                                    }
                                ]}
                                    staffInfo={staffInfo}
                                    onDelete={handleOpenModalDelete}
                                    onSend={handleOpenFormLeadershipSubmission}
                                />
                            </Panel>
                            <Panel header="Tăng lương" key="2">

                                <SalaryIncreaseCollapse
                                    staffInfo={staffInfo}
                                    onDelete={handleOpenModalDelete}
                                    onSend={handleOpenFormLeadershipSubmission}
                                />
                            </Panel>
                            <Panel header="Thăng chức" key="3">
                                <PromotedCollapse
                                    staffInfo={staffInfo}
                                    onDelete={handleOpenModalDelete}
                                    onSend={handleOpenFormLeadershipSubmission}
                                />
                            </Panel>
                            <Panel header="Đề xuất tham mưu" key="4">
                                <ProposedCollapse
                                    staffInfo={staffInfo}
                                    onDelete={handleOpenModalDelete}
                                    onSend={handleOpenFormLeadershipSubmission}
                                />
                            </Panel>
                        </Collapse>
                    </Space>
                </Row>


            </Modal>

            {formResignationApplication && (
                <ResignationApplicationForm
                    open={formResignationApplication}
                    onCancel={handleOpenFormResignationApplication}
                    staffInfo={staffInfo}
                />
            )}

            {formLeadershipSubmission && (
                <DialogSendLeader
                    open={formLeadershipSubmission}
                    onCancel={handleOpenFormLeadershipSubmission}
                    employeeId={staffInfo.employeeId}
                />
            )}

            {modalDelete && (
                <ModalDelete
                    open={modalDelete}
                    onCancel={handleOpenModalDelete}
                />
            )}
        </>
    );
}

export default ModalUpdateHappenings;
