import { Button, Col, Form, Image, Input, Row, Select, Upload } from "antd";
import React from "react";
import Styled from "./styled";

const { Option } = Select;

const getBase64 = (img, callback) => {
    const reader = new FileReader();
    reader.addEventListener("load", () => callback(reader.result));
    reader.readAsDataURL(img);
};

const beforeUpload = (file) => {
    const isJpgOrPng = file.type === "image/jpeg" || file.type === "image/png";
    if (!isJpgOrPng) {
        alert("You can only upload JPG/PNG file!");
    }
    const isLt2K = file.size / 1024 < 5;
    if (!isLt2K) {
        alert("Hình ảnh phải có kích thước nhỏ hơn 5Kb");
    }
    return isJpgOrPng && isLt2K;
}


const TabInfo = ({ listGender, fileImage, setFileImage }) => {

    const listTeam = [
        { value: 1, label: "Back-End" },
        { value: 2, label: "Front-End" },
        { value: 3, label: "Tester" }
    ];

    const handleChange = (info) => {
        if (info.file.status === "uploading") {
            return;
        }
        if (info.file.status === "done") {
            // Get this url from response in real world.
            getBase64(info.file.originFileObj, (url) => {
                setFileImage(url);
            });
        }
    };

    return (
        <Styled>
            <Row justify="space-between">
                <Col span={16}>
                    <Row gutter={[16, 0]}>
                        <Col className="gutter-row" span={8}>
                            <Form.Item
                                label='Tên nhân viên'
                                name="fullName"
                                rules={[
                                    {
                                        required: true,
                                        message: 'Bắt buộc phải nhập trường này!',
                                    },
                                    {
                                        pattern: '^([a-zA-Z ])*$',
                                        message: 'Chỉ chứa chữ không dấu',
                                    }
                                ]}
                            >
                                <Input style={{ width: "100%" }} />
                            </Form.Item>
                        </Col>
                        <Col className="gutter-row" span={8}>
                            <Form.Item
                                label="Mã nhân viên"
                                name="code"
                                rules={[
                                    {
                                        required: true,
                                        message: 'Bắt buộc phải nhập trường này!',
                                    },
                                    { whitespace: true, message: 'không được để trống' },
                                    { min: 6, message: 'Tối thiểu 6 kí tự', },
                                    { max: 10, message: 'Tối đa 10 kí tự' }
                                ]}
                            >
                                <Input style={{ width: "100%" }} />
                            </Form.Item>
                        </Col>
                        <Col className="gutter-row" span={8}>
                            <Form.Item
                                name="gender"
                                label="Giới tính"
                                rules={[
                                    {
                                        required: true,
                                        message: 'Bắt buộc phải chọn trường này!',
                                    },
                                ]}
                            >
                                <Select
                                    placeholder="Giới tính"
                                >
                                    {listGender.map(item => {
                                        return <Option key={item.value} value={item.value}>{item.label}</Option>;
                                    })}
                                </Select>
                            </Form.Item>
                        </Col>
                        <Col className="gutter-row" span={8}>
                            <Form.Item
                                label="Ngày sinh"
                                name="dateOfBirth"
                                rules={[
                                    {
                                        required: true,
                                        message: 'Bắt buộc phải chọn trường này!',
                                    },
                                ]}
                            >
                                <Input type="date" style={{ width: "100%" }} />
                            </Form.Item>
                        </Col>
                        <Col className="gutter-row" span={8}>
                            <Form.Item
                                label="Số điện thoại"
                                name="phone"
                                rules={[
                                    {
                                        required: true,
                                        message: 'Bắt buộc phải nhập trường này!',
                                    },
                                    { whitespace: true, message: 'không được để trống' },
                                    { max: 10, message: 'Tối đa 10 số' },
                                    {
                                        pattern: '^([-]?[0-9][1-9]*|0)$',
                                        message: 'Trường này phải là số',
                                    }
                                ]}
                            >
                                <Input style={{ width: "100%" }} />
                            </Form.Item>
                        </Col>

                        <Col className="gutter-row" span={8}>
                            <Form.Item
                                label='Nhóm'
                                name="teamId"
                                rules={[
                                    {
                                        required: true,
                                        message: 'Bắt buộc phải chọn trường này!',
                                    },
                                ]}
                            >
                                <Select
                                    placeholder="Chọn nhóm"
                                >
                                    {listTeam?.map(item => {
                                        return <Option key={item.value} value={item.value}>{item.label}</Option>;
                                    })}
                                </Select>
                            </Form.Item>
                        </Col>
                        <Col className="gutter-row" span={12}>
                            <Form.Item
                                label='Mã CCCD'
                                name="citizenId"
                                rules={[
                                    {
                                        required: true,
                                        message: 'Bắt buộc phải nhập trường này!',
                                    },
                                    { whitespace: true, message: 'không được để trống' },
                                    {
                                        pattern: '^([-]?[0-9][0-9]*|0)$',
                                        message: 'Trường này phải là số',
                                    },
                                    { max: 15, message: 'Tối đa 15 số' },
                                ]}
                            >
                                <Input style={{ width: "100%" }} />
                            </Form.Item>
                        </Col>
                        <Col className="gutter-row" span={12}>
                            <Form.Item
                                label="Email"
                                name="email"
                                rules={[
                                    {
                                        required: true,
                                        message: 'Bắt buộc phải nhập trường này!',
                                    },
                                    {
                                        type: 'email',
                                        message: 'Không đúng định dạng email!',
                                    }
                                ]}
                            >
                                <Input style={{ width: "100%" }} />
                            </Form.Item>
                        </Col>

                        <Col className="gutter-row" span={24}>
                            <Form.Item
                                label='Địa chỉ cụ thể'
                                name="address"
                                rules={[
                                    {
                                        required: true,
                                        message: 'Bắt buộc phải nhập trường này!',
                                    },
                                    { whitespace: true, message: 'không được để trống' },
                                ]}
                            >
                                <Input style={{ width: "100%" }} />
                            </Form.Item>
                        </Col>

                    </Row>
                </Col>
                <Col span={8}>
                    <Row>
                        <div className="avatar" >
                            <Image
                                src={fileImage?.toString()}
                            />
                        </div>
                    </Row>
                    <Row >
                        <Form.Item name="photoUrl">
                            <Upload
                                name="photoUrl"
                                action="https://www.mocky.io/v2/5cc8019d300000980a055e76"
                                onChange={handleChange}
                                beforeUpload={beforeUpload}
                            >
                                <Button >
                                    Thêm ảnh đại diện
                                </Button>
                            </Upload>
                        </Form.Item>
                    </Row>
                </Col>
            </Row >
        </Styled >
    )
}
export default TabInfo;
