import { Modal } from "antd"
import React from "react"
import styled from "styled-components"

const DialogDeleteFamilyRelationship = (props) => {

    const { open, listRelationship, setListRelationship, selectIndex, relationshipId, handleCloseDialogDeleteFamilyRelationship } = props;

    console.log("relationshipId", relationshipId);

    const handleDelete = () => {
        // console.log("selectIndex", selectIndex);
        if (relationshipId) {
            setListRelationship(listRelationship.filter((item) => item?.familyId.toString() !== relationshipId.toString()))
        } else {
            setListRelationship(listRelationship.filter((item) => item?.index.toString() !== selectIndex.toString()));
        }
        handleCloseDialogDeleteFamilyRelationship();
    }

    return (
        <>
            <StyledModal
                title="Xóa quan hệ gia đình"
                centered
                open={open}
                width={500}
                onCancel={handleCloseDialogDeleteFamilyRelationship}
                okText="Xác nhận"
                cancelText="Hủy"
                onOk={handleDelete}
            >
                <p>Bạn có chắc chắn muốn xóa?</p>
            </StyledModal>
        </>
    )
}

export default DialogDeleteFamilyRelationship;

const StyledModal = styled(Modal)`
    p {
        margin: 0;
    }

    .ant-btn-primary {
        background-color: #0d5ac3;
    }

    .ant-btn-primary:hover {
        background-color: #3365a9 !important;
    }

    .ant-modal-header {
        box-shadow: none;
    }

    .ant-modal-footer .ant-btn-default{
        background-color: orange;
    }
    .ant-modal-footer .ant-btn-default{
        background-color: #ffb833;
    }


    .ant-modal-footer {
        display: flex;
        justify-content: center;
        flex-direction: row-reverse;
        gap: 10px;
        box-shadow: none;
        margin: 0
    }
`
