import React from "react";
import { Modal, Button, Tabs, Row, Col, Table, Image } from "antd"
import { useState, useEffect } from "react";
import {
    GoogleSquareFilled, PhoneFilled, EnvironmentFilled
} from "@ant-design/icons"
import { HeaderStyled, ImageMaskCircle } from "./StaffAcceptedStyled"
import { getById, getForm } from "./StaffAcceptedService";
import styled from 'styled-components';
import moment from "moment";

// ========== Hồ sơ ===================
function RenderBrief(props) {
    const { data, dataForm } = props
    const employeeInfo = data?.employeeInfo
    const resume = dataForm?.resume
    const cv = dataForm?.cv

    if (!employeeInfo) {
        return <h1>No data</h1>
    } else {
        return (
            <div style={{ padding: '0 20px', overflowY: "scroll", height: 600 }}>
                <Row>
                    <Col span={8} style={{
                        display: "flex",
                        justifyContent: "center", alignItems: "center"
                    }}>
                        <ImageMaskCircle>
                            <Image
                                style={{ width: 150, height: 150, borderRadius: "50%" }}
                                src={resume?.photoUrl ? resume.photoUrl : "https://zos.alipayobjects.com/rmsportal/jkjgkEfvpUPVyRjUImniVslZfWPnJuuZ.png"}
                            />
                        </ImageMaskCircle>
                    </Col>

                    <Col span={16} style={{
                        display: "flex",
                        flexDirection: "column", justifyContent: "center", paddingLeft: 20
                    }}>
                        <h3>{employeeInfo.fullName}</h3>
                        <p style={{ fontSize: "16px", fontStyle: "italic" }}>
                            {employeeInfo.teamId === 1 && "Back-End"}
                            {employeeInfo.teamId === 2 && "Front-End"}
                            {employeeInfo.teamId === 3 && "Tester"}
                        </p>
                        <p>{cv?.hobby ? cv.hobby : "Null"}</p>
                    </Col>
                </Row>

                <Row style={{ backgroundColor: "#289928", color: "#fff", margin: "10px 0", padding: "0 10px" }}>
                    <Col style={{ textAlign: "left", padding: "5px 0" }} span={8}><GoogleSquareFilled /> {employeeInfo.email}</Col>
                    <Col style={{ textAlign: "center", padding: "5px 0" }} span={8}><PhoneFilled /> {employeeInfo.phone}</Col>
                    <Col style={{ textAlign: "center", padding: "5px 0" }} span={8}><EnvironmentFilled /> {employeeInfo.address ? employeeInfo.address : "Hà Nội"}</Col>
                </Row>

                <div>
                    <h4>Mục tiêu</h4>
                    <p>{cv?.careerGoal ? cv.careerGoal : "Null"}</p>
                </div>

                <div>
                    <h4>Các kỹ năng</h4>
                    <p>{cv?.skill ? cv.skill : "Null"}</p>
                </div>

                <div>
                    <h4>Kinh nghiệm làm việc</h4>
                    <div style={{ paddingBottom: 50 }}>
                        {cv?.workExperiences?.length === 0 ? "Null" :
                            cv?.workExperiences.map((item, index) => (
                                <div key={index}>
                                    <span style={{ display: "block" }}>
                                        <span style={{ display: "inline-block", width: "30%" }}> - Công ty: {item.company}</span>
                                        <span style={{ display: "inline-block", width: "30%" }}>Vị trí: {item.position}</span>
                                        <span style={{ display: "inline-block", width: "30%" }}>Mô tả: {item.detail}</span>
                                    </span>
                                    <span style={{ display: "block" }}>
                                        <span style={{ display: "inline-block", width: "30%", whiteSpace: "pre-wrap" }}>
                                            Ngày bắt đầu:
                                            {" " + moment(item.startDate, 'YYYY-MM-DD').format('DD-MM-YYYY')}
                                        </span>
                                        <span style={{ display: "inline-block", width: "30%" }}>
                                            Ngày kết thúc:
                                            {" " + moment(item.endDate, 'YYYY-MM-DD').format('DD-MM-YYYY')}
                                        </span>
                                    </span>
                                    <br></br>
                                </div>
                            ))}
                    </div>
                </div>
            </div>
        )
    }
}

// ========== Sơ yếu lý lịch ==========
function RenderCurriculumVitae(props) {
    const { data, dataForm } = props
    const employeeInfo = data?.employeeInfo
    const familyRelations = data?.familyRelations
    const resume = dataForm?.resume

    if (!employeeInfo) {
        return <h1>No data</h1>
    } else {
        return (
            <div style={{ overflowY: "scroll", height: '81vh', paddingBottom: '70px' }}>
                <div>
                    <div>
                        <h4 style={{ textAlign: "center", fontSize: 18, lineHeight: 1.2 }}>CỘNG HÒA XÃ HỘI CHỦ NGHĨA VIỆT NAM</h4>
                        <div style={{ display: "flex", justifyContent: "center" }}>
                            <h5 style={{
                                textAlign: "center", width: 220, borderBottom: "2px solid black",
                                paddingBottom: 5
                            }}>Độc lập - Tự do - Hạnh Phúc</h5>
                        </div>
                    </div>

                    <Row>
                        <Col span={6}>
                            <Image
                                style={{ width: 150, height: 200 }}
                                src={employeeInfo?.photoUrl ? employeeInfo.photoUrl : "https://zos.alipayobjects.com/rmsportal/jkjgkEfvpUPVyRjUImniVslZfWPnJuuZ.png"}
                            />
                        </Col>
                    </Row>

                    <div>
                        <h3 style={{ textAlign: "center", fontSize: 20 }}>SƠ YẾU LÝ LỊCH</h3>
                        <h3 style={{ textAlign: "center", fontSize: 16, fontStyle: "italic" }}>TỰ THUẬT</h3>
                    </div>

                    {/* thông tin cá nhân */}
                    <div>
                        <h4>I. THÔNG TIN BẢN THÂN</h4>
                        <div style={{ paddingLeft: 15 }}>
                            <p>
                                <span style={{ width: "60%", display: "inline-block" }}>1. Họ và tên: {employeeInfo.fullName}</span>
                                <span>Nam, nữ: {employeeInfo.gender === 0 ? "Nam" : "Nữ"}</span>
                            </p>
                            <p>2. Sinh ngày: {employeeInfo.dateOfBirth ? moment(employeeInfo.dateOfBirth, 'YYYY-MM-DD').format('DD-MM-YYYY') : "Null"}</p>
                            <p>3. Nguyên quán: {employeeInfo.address ? employeeInfo.address : "Null"}</p>
                            <p>
                                <span style={{ width: "30%", display: "inline-block" }}>4. Số CCCD: {resume?.citizenId ? resume.citizenId : "Null"}</span>
                                <span style={{ width: "30%", display: "inline-block" }}>Cấp ngày: {resume.citizenIdIssuanceDate ? moment(resume.citizenIdIssuanceDate, "YYYY-MM-DD").format("DD-MM-YYYY") : "Null"}</span>
                                <span>Nơi cấp: {resume?.citizenIdIssuingAuthority ? resume.citizenIdIssuingAuthority : "Null"}</span>
                            </p>
                            <p>5. Số điện thoại liên hệ: {employeeInfo.phone}</p>
                            <p>6. Email: {employeeInfo.email}</p>
                            <p>
                                <span style={{ display: "inline-block", width: "30%" }}>7. Dân tộc: {resume?.ethnicity ? resume.ethnicity : "Null"}</span>
                                <span>Tôn giáo: {resume?.religion ? resume.relation : "Null"}</span>
                            </p>
                        </div>
                    </div>

                    {/* thông tin gia đình */}
                    <div>
                        <h4>II. QUAN HỆ GIA ĐÌNH</h4>
                        {familyRelations.length === 0 ? "Null" :
                            familyRelations.map((item, index) => (
                                <div key={index} style={{ paddingLeft: 15 }}>
                                    <p style={{ fontWeight: 600 }}>{++index}. Quan hệ: {item.relation}</p>
                                    <p>
                                        <span style={{ width: "30%", display: "inline-block" }}> - Họ và tên: {item.name}</span>
                                        <span style={{ width: "30%", display: "inline-block" }}>Nam, nữ: {item.gender ? "Nam" : "Nữ"}</span>
                                        <span>Sinh ngày: {item.dateOfBirth ? moment(item.dateOfBirth, "YYYY-MM-DD").format("DD-MM-YYYY") : "Null"}</span>
                                    </p>
                                    <p>
                                        <span style={{ width: "30%", display: "inline-block" }}> - CCCD: {item.citizenId}</span>
                                        <span style={{ width: "30%", display: "inline-block" }}>Địa chỉ: {item.address}</span>
                                    </p>
                                    <p>
                                        <span style={{ width: "30%", display: "inline-block" }}> - Số điện thoại: {item.phone ? item.phone : "Null"}</span>
                                        <span style={{ width: "30%", display: "inline-block" }}>Email: {item.email ? item.email : "Null"}</span>
                                    </p>
                                </div>
                            ))
                        }
                    </div>
                </div>
            </div>
        )
    }
}

// ========== Văn bằng ================
const Columns = () => [
    {
        title: 'Tên văn bằng',
        dataIndex: 'name',
    },
    {
        title: 'Lĩnh vực',
        dataIndex: 'field',
    },
    {
        title: 'Loại',
        dataIndex: 'graduatedWith',
    },
    {
        title: "Trường",
        dataIndex: 'educationalOrg'
    },
    {
        title: 'Ngày cấp',
        render: (rowData) => rowData.educationStartDate ? moment(rowData.educationStartDate, "YYYY-MM-DD").format("DD-MM-YYYY") : rowData.educationStartDate
    },
    {
        title: "Ngày hết hạn",
        render: (rowData) => rowData.educationEndDate ? moment(rowData.educationEndDate, "YYYY-MM-DD").format("DD-MM-YYYY") : rowData.educationEndDate
    }
]

function RenderDegrees(props) {
    const { columns, data } = props
    const certificates = data?.certificates

    return (
        <HeaderStyled>
            <Table
                columns={columns}
                dataSource={certificates}
                pagination={{ defaultPageSize: 10 }}
                scroll={{ y: 450 }}
            />
        </HeaderStyled>
    )
}

function StaffAcceptedDialog(props) {


    const { open, item, onOk, onCancel } = props
    const [employee, setEmployee] = useState()
    const [employeeForm, setemployeeForm] = useState()
    const columns = Columns()

    useEffect(() => {
        const getInfo = async () => {
            const res = await getById(item.employeeId)
            setEmployee(res.data.data)
        }
        const getInfoForm = async () => {
            const res = await getForm(item.employeeId)
            setemployeeForm(res.data.data)
        }
        getInfo()
        getInfoForm()
    }, [])

    return (
        <div>
            <StyledModal
                centered
                title="Hồ sơ nhân viên"
                width={1000}
                open={open}
                // onOk={onOk}
                onCancel={onCancel}
                footer={
                    <div style={{ display: "flex", justifyContent: "center" }}>
                        <Button key="cancel" type="primary" onClick={onCancel}
                            style={{ backgroundColor: "orange", color: "white" }}
                        >
                            Hủy
                        </Button>
                    </div>
                }
            >
                <div>
                    <Tabs
                        defaultActiveKey="1"
                        tabPosition="left"
                        style={{
                            height: 600,
                        }}
                        tabBarStyle={{ width: 200, whiteSpace: "break-spaces" }}
                        items={[
                            {
                                label: "HỒ SƠ",
                                key: 1,
                                children: <RenderBrief data={employee} dataForm={employeeForm} />
                            },
                            {
                                label: "SƠ YẾU LÝ LỊCH",
                                key: 2,
                                children: <RenderCurriculumVitae data={employee} dataForm={employeeForm} />
                            },
                            {
                                label: "DANH SÁCH VĂN BẰNG",
                                key: 3,
                                children: <RenderDegrees columns={columns} data={employee} />
                            },
                        ]}
                    />
                </div>
            </StyledModal>
        </div>
    )
}

export default StaffAcceptedDialog

const StyledModal = styled(Modal)`
    .ant-modal-content{
        overflow: hidden;
        height: 90vh;
    }
    .ant-modal-body{
        min-height: 76vh;
        overflow: hidden;
    }

    .ant-modal-footer{
        position: absolute;
        bottom: 0;

    }
`;
