import { Col, Image, Row, Table } from "antd";
import React from "react";
import moment from "moment";

const CurriculumVitae = ({ employeeInfo, employeeResume }) => {
    const listGender = [
        { value: 0, label: 'Nam' },
        { value: 1, label: 'Nữ' },
    ];

    const columns = [
        { key: "name", title: "Họ và tên", dataIndex: "name", width: "19%" },
        {
            key: "dateOfBirth", title: "Ngày sinh", dataIndex: "dateOfBirth", width: "15%",
            render: (rowData) => {
                return (
                    <>{moment(rowData).format("DD-MM-YYYY")}</>
                )
            }
        },
        {
            key: "gender", title: "Giới tính", dataIndex: "gender", width: "12%",
            render: (gender) => {
                const labelGender = listGender.find((item) => item.value === gender)

                return (
                    <>{labelGender.label}</>
                )
            }
        },
        { key: "relation", title: "Quan hệ", dataIndex: "relation", width: "12%" },
        { key: "citizenId", title: "Số CCCD/CMT", dataIndex: "citizenId", width: "17%" },
        { key: "address", title: "Địa chỉ", dataIndex: "address", width: "25%" },
    ];

    return (
        <div style={{ overflowY: "scroll", height: '76vh', paddingRight: '24px' }}>
            <Row>
                <Col span={6}>
                    <Image
                        style={{ width: 150, height: 200 }}
                        src={"https://zos.alipayobjects.com/rmsportal/jkjgkEfvpUPVyRjUImniVslZfWPnJuuZ.png"}
                    />
                </Col>

                <Col span={18}>
                    <div
                        style={{
                            display: 'flex',
                            flexDirection: 'column',
                            justifyContent: 'center',
                            textAlign: 'center',
                        }}
                    >
                        <h4 style={{ fontSize: 20 }}>CỘNG HÒA XÃ HỘI CHỦ NGHĨA VIỆT NAM</h4>
                        <h5>
                            <span style={{ borderBottom: '2px solid #4d4d4d', paddingBottom: '5px' }}>
                                Độc lập - Tự do - Hạnh Phúc
                            </span>
                        </h5>
                        <h3 style={{ fontSize: 20, marginTop: '30px' }}>SƠ YẾU LÝ LỊCH</h3>
                        <h3 style={{ fontSize: 16 }}>TỰ THUẬT</h3>
                    </div>
                </Col>
            </Row>

            {/* thông tin cá nhân */}
            <div style={{ marginTop: '30px' }}>
                <h4>I. THÔNG TIN BẢN THÂN</h4>
                <div style={{ paddingLeft: 15, marginTop: '15px' }}>
                    <p>
                        <span style={{ width: "60%", display: "inline-block" }}>1. Họ và tên: {employeeResume?.fullName}</span>
                        <span>Nam, nữ: {employeeResume?.gender === 0 ? "Nam" : "Nữ"}</span>
                    </p>
                    <p>
                        <span style={{ width: "30%", display: "inline-block" }}>3. Sinh ngày: {moment(employeeResume?.dateOfBirth).format("DD-MM-YYYY")}</span>
                        <span style={{ width: "30%", display: "inline-block" }}>Nơi sinh: {employeeResume?.address}</span>
                    </p>
                    <p>3. Nguyên quán: {employeeResume?.currentAddress}</p>
                    <p>
                        <span style={{ width: "30%", display: "inline-block" }}>4. Số CCCD: {employeeResume.citizenId}</span>
                        <span style={{ width: "30%", display: "inline-block" }}>Cấp ngày: {moment(employeeResume.citizenIdIssuanceDate).format("DD-MM-YYYY")}</span>
                        <span>Nơi cấp: {employeeResume.citizenIdIssuingAuthority}</span>
                    </p>
                    <p>5. Số điện thoại liên hệ: {employeeResume?.phone}</p>
                    <p>6. Email: {employeeResume?.email}</p>
                    <p>
                        <span style={{ display: "inline-block", width: "30%" }}>7. Dân tộc: {employeeResume.ethnicity}</span>
                        <span>Tôn giáo: {employeeResume.religion}</span>
                    </p>
                </div>
            </div>

            {/* thông tin gia đình */}
            <div>
                <h4 style={{ margin: '20px 0' }}>II. QUAN HỆ GIA ĐÌNH</h4>

                <Table
                    dataSource={employeeInfo?.familyRelations}
                    columns={columns}
                    pagination={false}
                />
            </div>

            <div>
                <p style={{ margin: '15px 0 0' }}>
                    Tôi xin cam đoan bản khai sơ yếu lý lịch trên đúng sự thật, nếu có điều gì không đúng tôi chịu trách
                    nhiệm trước pháp luật về lời khai của mình.
                </p>
                <div className="modal_signature">
                    <p>
                        <span>Tp Ha noi, </span>
                        <span>ngày 12 </span>
                        <span>tháng 04 </span>
                        <span>năm 2023</span>
                    </p>
                    <p>Người khai</p>
                    <p>{employeeResume?.commonName}</p>
                    <p>{employeeResume?.fullName}</p>

                </div>
            </div>
        </div >
    )
}

export default CurriculumVitae;
