import axios from 'axios';
import ConstantList from '../../appConfig';
const API_PATH_employees = ConstantList.API_ENPOINT;

export const getStaff = (data) => {
    const url = `${API_PATH_employees}/employees?statuses=5&page=1&size=20`;

    return axios.get(url, data);
};

export const updateStaff = (data) => {
    const url = `${API_PATH_employees}/employees/${data.employeeId}`;

    return axios.put(url, data);
};

export const getStaffById = (data) => {
    const url = `${API_PATH_employees}/employees/${data.employeeId}`;

    return axios.get(url, data);
};

export const getSalaryStaff = (data) => {
    const url = `${API_PATH_employees}/employees/${data.employeeId}/increase-salary?page=1&size=20`;

    return axios.get(url, data);
};

export const addSalaryStaff = (data) => {
    const url = `${API_PATH_employees}/employees/${data.employeeId}/increase-salary?page=1&size=20`;

    return axios.post(url, data);
};

export const updateSalaryStaff = (data) => {
    const url = `${API_PATH_employees}/employees/increase-salary/${data.employeeId}`;

    return axios.put(url, data);
};

export const updateStatusSalaryStaff = (data) => {
    const url = `${API_PATH_employees}/employees/increase-salary/${data.employeeId}/stt?status=3`;

    return axios.put(url, data);
};

export const getProposedStaff = (data) => {
    const url = `${API_PATH_employees}/employees/${data.employeeId}/propose-consult?page=1&size=20`;

    return axios.get(url, data);
};

export const addProposedStaff = (data) => {
    const url = `${API_PATH_employees}/employees/${data.employeeId}/propose-consult?page=1&size=20`;

    return axios.post(url, data);
};

export const updateProposedStaff = (data) => {
    const url = `${API_PATH_employees}/employees/propose-consult/${data.employeeId}`;

    return axios.put(url, data);
};

export const updateStatusProposedStaff = (data) => {
    const url = `${API_PATH_employees}/employees/propose-consult/${data.employeeId}/stt?status=2`;

    return axios.put(url, data);
};

export const getPromotedStaff = (data) => {
    const url = `${API_PATH_employees}/employees/${data.employeeId}/promote?page=1&size=20`;

    return axios.get(url, data);
};

export const addPromotedStaff = (data) => {
    const url = `${API_PATH_employees}/employees/${data.employeeId}/promote?page=1&size=20`;

    return axios.post(url, data);
};

export const updatePromotedStaff = (data) => {
    const url = `${API_PATH_employees}/employees/promote/${data.employeeId}`;

    return axios.put(url, data);
};

export const updateStatusPromotedStaff = (data) => {
    const url = `${API_PATH_employees}/employees/promote/${data.employeeId}/stt?status=2`;

    return axios.put(url, data);
};
