import { Table } from "antd";
import moment from "moment";
import React from "react";
import Styled from "./Styled";

const DiplomaInfo = (props) => {
    const { employeeInfo } = props

    const columns = [
        { key: "name", title: "Tên văn bằng", dataIndex: "name", width: "20%" },
        { key: "content", title: "Nội dung", dataIndex: "content", width: "22%" },
        { key: "educationalOrg", title: "Nơi cấp", dataIndex: "educationalOrg", width: "18%" },
        {
            key: "issuanceDate", title: "Ngày cấp", dataIndex: "issuanceDate", width: "20%",
            render: (rowData) => {
                return (
                    <>{moment(rowData).format("DD-MM-YYYY")}</>
                )
            }
        },
        { key: "field", title: "Lĩnh vực", dataIndex: "field", width: "20%" },
    ];

    return (
        <Styled>
            <Table
                dataSource={employeeInfo?.certificates}
                columns={columns}
                pagination={false}
                style={{ paddingRight: '24px' }}
            ></Table>
        </Styled>
    )
}

export default DiplomaInfo;
