import { Form, Input, Modal } from 'antd';
import React from 'react';
import { useState } from 'react';
import { updateStaff } from './UpdateHappeningsService';

function ResignationApplicationForm({ open, onCancel, staffInfo }) {
    const [dataForm, setDataForm] = useState({
        employeeId: staffInfo?.employeeId || '',
        fullName: staffInfo?.fullName || '',
        registerPosition: staffInfo?.registerPosition || '',
        terminatedDate: staffInfo?.terminatedDate || '',
        terminatedReason: staffInfo?.terminatedReason || '',
    });

    const onChangeValue = (e) => {
        setDataForm({ ...dataForm, [e.target.name]: e.target.defaultValue });
    };

    const handleSubmit = () => {
        let dataNew = {
            ...dataForm,
            status: 12,
        };

        const res = updateStaff(dataNew);
        if (res?.data?.data === 2000) {
            console.log('123');
            onCancel();
        } else {
            console.log('aaa');
        }
    };

    return (
        <Modal
            className="information_modal"
            centered
            okText="Trình lãnh đạo"
            title="Biểu mẫu"
            cancelText="HỦY"
            open={open}
            onOk={handleSubmit}
            onCancel={onCancel}
            width={'70vw'}
        >
            <Form>
                <div style={{ display: 'flex', flexDirection: 'column', textAlign: 'center' }}>
                    <h4 style={{ textTransform: 'uppercase', fontSize: '20px' }}>Cộng HÒA XÃ HỘI CHỦ NGHĨA VIỆT NAM</h4>
                    <p>
                        <span style={{ fontSize: '18px', borderBottom: '2px solid #4d4d4d', paddingBottom: '5px' }}>
                            Độc lập - Tự do - Hạnh Phúc
                        </span>
                    </p>
                </div>
                <div className="modal_content">
                    <h4
                        style={{
                            textTransform: 'uppercase',
                            textAlign: 'center',
                            padding: '30px 0',
                            fontSize: '18px',
                        }}
                    >
                        Đơn xin nghỉ việc
                    </h4>
                    <p>Kính gửi: Ban Giám đốc công ty OceanTech</p>

                    <Form.Item
                        onChange={onChangeValue}
                        name="fullName"
                        label="Tôi tên là: "
                        rules={[
                            {
                                required: true,
                                message: 'trường này không được để trống',
                            },
                            {
                                max: 10,
                                message: 'trường này chứa tối đa 10 kí tự',
                            },
                        ]}
                    >
                        <Input defaultValue={dataForm.fullName} />
                    </Form.Item>
                    <div>
                        <Form.Item
                            onChange={onChangeValue}
                            name="registerPosition"
                            label="Hiện đang công tác tại vị trí: "
                        >
                            <Input defaultValue={dataForm.registerPosition} />
                        </Form.Item>
                    </div>
                    <div>
                        <Form.Item onChange={onChangeValue} name="time" label="Tôi xin được nghỉ việc từ ngày: ">
                            <Input />
                        </Form.Item>
                    </div>
                    <div className="form_block">
                        <Form.Item
                            onChange={onChangeValue}
                            name="reason"
                            label="Tôi làm đơn này đề nghị ban giám đốc cho tôi xin nghỉ việc với lý do: "
                        >
                            <Input />
                        </Form.Item>
                    </div>
                    <div
                        style={{
                            display: 'inline-block',
                            marginTop: '35px',
                        }}
                    >
                        <p>
                            Trong khi chờ đợi sự chấp thuận của Ban Giám Đốc Công ty, tôi sẽ tiếp tục làm việc nghiêm
                            túc và tiến hành bàn giao công việc cũng như tài sản cho người quản lý trực tiếp của tôi.
                        </p>
                        <p>Tôi xin chân thành cảm ơn.</p>
                    </div>
                </div>
                <div className="modal_signature">
                    <div
                        className="date-signature"
                        style={{ display: 'flex', alignItems: 'center', marginBottom: '15px' }}
                    >
                        Hà nội,
                        <Form.Item name="day" onChange={onChangeValue}>
                            <Input className="day" />
                        </Form.Item>
                        ,
                        <Form.Item name="date" onChange={onChangeValue}>
                            <Input />
                        </Form.Item>
                        tháng
                        <Form.Item name="month" onChange={onChangeValue}>
                            <Input />
                        </Form.Item>
                        ,
                        <Form.Item name="year" onChange={onChangeValue}>
                            <Input className="year" />
                        </Form.Item>
                    </div>
                    <p>Người làm đơn</p>
                    <p>{staffInfo.fullName.split(' ').slice(-1).join(' ')}</p>
                    <p>{staffInfo.fullName}</p>
                </div>
            </Form>
        </Modal>
    );
}

export default ResignationApplicationForm;
