import React from 'react';
import { DatePicker, Form, Input, Modal } from 'antd';
import styled from 'styled-components';

function ModalSaveRecords({ open, onCancel }) {
    const [form] = Form.useForm();

    const onOk = (value) => {
        console.log('aaa');
    };

    return (
        <StyledModal
            centered
            okText="Lưu"
            title="lưu thông tin"
            cancelText="HỦY"
            open={open}
            onOk={onOk}
            onCancel={onCancel}
            width={'500'}
        >
            <Form
                form={form}
                onFinish={onOk}
                style={{ display: 'flex', justifyContent: 'center', gap: '20px', alignItems: 'center' }}
            >
                <Form.Item
                    label="Ngày nộp lưu"
                    rules={[
                        {
                            required: true,
                        },
                    ]}
                >
                    <DatePicker placeholder="YYYY-MM-DD" format="YYYY-MM-DD" />
                </Form.Item>
                <Form.Item
                    label="Số nộp lưu"
                    rules={[
                        {
                            required: true,
                        },
                    ]}
                >
                    <Input />
                </Form.Item>
            </Form>
        </StyledModal>
    );
}

export default ModalSaveRecords;

const StyledModal = styled(Modal)`
    .ant-modal-body {
        padding: 30px 20px;
    }
    .ant-form-item,
    label {
        margin-bottom: 0;
    }
    .ant-modal-footer {
        margin-top: 0;
        box-shadow: unset;
    }
    .ant-modal-footer .ant-btn-primary {
        background-color: green;
    }

    .ant-modal-footer .ant-btn-primary:not(:disabled):hover {
        background-color: #009900;
    }
`;
