import { Button, Col, Form, Input, Modal, Row } from 'antd';
import { sendLeaderStaff } from 'app/views/create-staff/CreateStaffService';
import React from 'react';
import styled from 'styled-components';
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

toast.configure({
    autoClose: 1000,
    draggable: false,
    limit: 3,
});

const DialogSendLeader = ({ open, employeeId, onCancel }) => {
    const [form] = Form.useForm();

    const handleSubmitRegister = async (value) => {
        const res = await sendLeaderStaff({ employeeId, value });
        if (res?.data?.code === 200) {
            if (res?.data?.code === 200) {
                toast.success(res?.data?.message);
                // handleClose()
                // handleCloseDialogRegister()
                // handleClose()
            } else {
                toast.warning(res?.data?.message);
            }
        }
    };

    return (
        <StyledModal title="Trình lãnh đạo" open={open} width={700} onCancel={onCancel} footer={null}>
            <Form
                form={form}
                onFinish={handleSubmitRegister}
                layout="vertical"
                // footer={null}
            >
                <Row justify="space-between">
                    <Col span={6}>
                        <Form.Item
                            label="Ngày trình"
                            name="registerDate"
                            rules={[
                                {
                                    required: true,
                                    message: 'Bắt buộc phải nhập trường này!',
                                },
                            ]}
                        >
                            <Input type="date" style={{ width: '100%' }} />
                        </Form.Item>
                    </Col>
                    <Col span={10}>
                        <Form.Item
                            label="Tên nhân viên"
                            name="registerName"
                            rules={[
                                {
                                    required: true,
                                    message: 'Bắt buộc phải nhập trường này!',
                                },
                            ]}
                        >
                            <Input style={{ width: '100%' }} />
                        </Form.Item>
                    </Col>
                    <Col span={8}>
                        <Form.Item
                            label="Chức vụ"
                            name="registerPosition"
                            rules={[
                                {
                                    required: true,
                                    message: 'Bắt buộc phải nhập trường này!',
                                },
                            ]}
                        >
                            <Input style={{ width: '100%' }} />
                        </Form.Item>
                    </Col>
                    <Col span={24}>
                        <Form.Item
                            label="Nội dung trình lãnh đạo"
                            name="registerContent"
                            rules={[
                                {
                                    required: true,
                                    message: 'Bắt buộc phải nhập trường này!',
                                },
                            ]}
                        >
                            <Input style={{ width: '100%' }} />
                        </Form.Item>
                    </Col>
                </Row>
                <div className="btn-end-position">
                    <Button className="btn-register" htmlType="submit">
                        TRÌNH LÃNH ĐẠO
                    </Button>
                    <Button className="btn-close" onClick={onCancel}>
                        HỦY
                    </Button>
                </div>
            </Form>
        </StyledModal>
    );
};

export default DialogSendLeader;

const StyledModal = styled(Modal)`
    .ant-modal-title {
        font-size: 24px;
        margin-bottom: 14px;
    }
    .ant-modal-content {
        min-height: auto;
        max-height: auto;
    }
    .ant-modal-body {
        padding: 0;
    }
    .btn-close {
        background-color: orange;
        color: #fff;
    }
    .btn-close:hover {
        background-color: #ffb833;
        color: #fff;
    }
    .btn-register {
        background-color: #0d5ac3;
        color: #fff;
    }
    .btn-register:hover {
        background-color: #3365a9;
        color: #fff;
    }
`;
