import { DeleteFilled, EditFilled, } from "@ant-design/icons";
import { Button, Col, Form, Input, Row, Table, Tooltip } from "antd";
import { useTranslation } from "react-i18next";
import React, { useState } from "react";
import Styled from "./styled";
import moment from "moment";
import DialogDeleteText from "./dialog-delete-text";

const TabTextInfo = (props) => {

    const { t } = useTranslation();

    const { listDiploma, setListDiploma } = props;

    const [diplomaId, setDiplomaId] = useState(0);

    const [selectIndex, setSelectIndex] = useState();

    const [shouldOpenDialogDeleteText, setShouldOpenDialogDeleteText] = useState(false);

    const [form] = Form.useForm();

    const onFinish = (value) => {

        if (typeof selectIndex === "number") {
            const dataUpdate = listDiploma.map((item, index) => {
                const itemUpdate = index === selectIndex ? { ...item, ...value } : item
                return {
                    ...itemUpdate,
                };
            });
            setListDiploma([...dataUpdate]);

        }
        else {
            const newItemWithIndex = { ...value, index: listDiploma.length.toString() };
            setListDiploma([
                ...listDiploma,
                newItemWithIndex
            ]);
        }

        form.resetFields();
        setSelectIndex("")
    };

    const columns = [
        {
            key: "action",
            title: t("general.action"),
            dataIndex: "action",
            width: "10%",
            render: (rowDataTextInfo, recordTextInfo, index) => {
                return (
                    <div style={{ display: "flex" }} >
                        <Tooltip placement="bottom" title="chỉnh sửa">
                            <EditFilled
                                style={{ color: "#0d4ca5" }}
                                onClick={() => onClickEditTextInfo(recordTextInfo, index)}
                            />
                        </Tooltip>
                        <Tooltip placement="bottom" title="xóa">
                            <DeleteFilled
                                style={{ color: "red" }}
                                onClick={() => onClickDeleteTextInfo(recordTextInfo, index)}
                            />
                        </Tooltip>
                    </div>
                )
            }
        },
        { key: "name", title: "Tên văn bằng", dataIndex: "name", width: "20%" },
        { key: "content", title: "Nội dung văn bằng", dataIndex: "content", width: "20%" },
        { key: "educationalOrg", title: "Nơi cấp", dataIndex: "educationalOrg", width: "20%" },
        {
            key: "issuanceDate", title: "Ngày cấp", dataIndex: "issuanceDate", width: "10%",
            render: (rowData) => {
                return (
                    <>{moment(rowData).format("DD-MM-YYYY")}</>
                )
            }
        },
        { key: "field", title: "Lĩnh vực", dataIndex: "field", width: "10%" },
    ]

    const onClickEditTextInfo = (record, index) => {

        // const dataRecord = record;
        form.setFieldsValue({
            name: record?.name,
            content: record?.content,
            educationalOrg: record?.educationalOrg,
            issuanceDate: record?.issuanceDate?.split(" ")[0],
            field: record?.field
        });
        setSelectIndex(index);
    };

    const onClickDeleteTextInfo = (recordTextInfo, index) => {
        setDiplomaId(recordTextInfo?.certificateId)
        setShouldOpenDialogDeleteText(true)
        setSelectIndex(index);
        form.resetFields();
    }

    const handleCloseDialogDeleteText = () => {
        setShouldOpenDialogDeleteText(false)
    }

    return (
        <Styled>
            {shouldOpenDialogDeleteText && (
                <DialogDeleteText
                    open={shouldOpenDialogDeleteText}
                    handleCloseDialogDeleteText={handleCloseDialogDeleteText}
                    listDiploma={listDiploma}
                    setListDiploma={setListDiploma}
                    selectIndex={selectIndex}
                    diplomaId={diplomaId}
                />
            )}

            <Form
                wrapperCol={{
                    span: 24,
                }}
                onFinish={onFinish}
                layout="vertical"
                form={form}
            >
                <Row>
                    <Col className="gutter-row" span={6}>
                        <Form.Item
                            label='Tên văn bằng'
                            name="name"
                            rules={[
                                {
                                    required: true,
                                    message: 'Bắt buộc phải nhập trường này!',
                                },
                                { whitespace: true, message: 'không được để khoảng trằng trống' },
                            ]}
                        >
                            <Input style={{ width: "100%" }} />
                        </Form.Item>
                    </Col>

                    <Col className="gutter-row" span={6}>
                        <Form.Item
                            label='Nơi cấp'
                            name="educationalOrg"
                            rules={[
                                {
                                    required: true,
                                    message: 'Bắt buộc phải nhập trường này!',
                                },
                                { whitespace: true, message: 'không được để khoảng trằng trống' },
                            ]}
                        >
                            <Input style={{ width: "100%" }} />
                        </Form.Item>
                    </Col>
                    <Col className="gutter-row" span={6}>
                        <Form.Item
                            label="Ngày cấp"
                            name="issuanceDate"
                            rules={[
                                {
                                    required: true,
                                    message: 'Bắt buộc phải nhập trường này!',
                                },
                            ]}
                        >
                            <Input type="date" style={{ width: "100%" }} />
                        </Form.Item>
                    </Col>
                    <Col className="gutter-row" span={6}>
                        <Form.Item
                            label='Lĩnh vực'
                            name="field"
                            rules={[
                                {
                                    required: true,
                                    message: 'Bắt buộc phải nhập trường này!',
                                },
                                { whitespace: true, message: 'không được để khoảng trằng trống' },
                            ]}
                        >
                            <Input style={{ width: "100%" }} />
                        </Form.Item>
                    </Col>
                    <Col className="gutter-row" span={18}>
                        <Form.Item
                            label='Nội dung văn bằng'
                            name="content"
                            rules={[
                                {
                                    required: true,
                                    message: 'Bắt buộc phải nhập trường này!',
                                },
                                { whitespace: true, message: 'không được để khoảng trằng trống' },
                            ]}
                        >
                            <Input style={{ width: "100%" }} />
                        </Form.Item>
                    </Col>
                    <Col className="gutter-row btn-form" span={6}>
                        <Form.Item>
                            <div className="btn-text-info">
                                <Button className="btn-add" htmlType="submit">
                                    Thêm
                                </Button>
                                <Button className="btn-reset" onClick={() => form.resetFields()}>
                                    Đặt lại
                                </Button>
                            </div>

                        </Form.Item>
                    </Col>
                </Row>

            </Form>

            <Table scroll={{ y: 250 }} rowKey="index" dataSource={listDiploma} columns={columns} pagination={false} ></Table>


        </Styled>
    )
}
export default TabTextInfo;
