import { EnvironmentFilled, GoogleSquareFilled, PhoneFilled } from '@ant-design/icons';
import { Col, Image, Row } from 'antd';
import { isEmpty } from 'lodash';
import React, { useState } from 'react';
import { useEffect } from 'react';
import { getStaffById } from '../update-happenings/UpdateHappeningsService';

const EndStaffProfile = ({ employeeId }) => {
    const [employeeInfo, setEmployeeInfo] = useState([]);

    const handleGetStaffById = async (employeeId) => {
        const res = await getStaffById({ employeeId });

        if (res?.data?.code === 200) {
            setEmployeeInfo(res?.data?.data);
        }
    };

    useEffect(() => {
        if (employeeId) {
            handleGetStaffById(employeeId);
        }
    }, []);

    const dataCV = {
        careerGoal: 'Trở thành một game thủ chuyên nghiệp thi đấu cho GAM ESPORTS',
        skill: 'HTML, CSSS, JS',
        hobby: 'Nhảy dây, đá bóng, đánh bài',
        workExperiences: [
            {
                company: 'Mitsu yanako',
                position: 'Intern',
                detail: 'Thực hiện 2 dự án',
                startDate: '2022-09-06',
                endDate: '2022-09-07',
            },
            {
                company: 'Monika yasuo',
                position: 'Fresher',
                detail: 'Thực hiện 5 dự án',
                startDate: '2021-03-03',
                endDate: '2022-05-08',
            },
            {
                company: 'Otika',
                position: 'Leader',
                detail: 'Thực hiện 12 dự án',
                startDate: '2020-09-04',
                endDate: '2022-06-02',
            },
            {
                company: 'Otika',
                position: 'Leader',
                detail: 'Thực hiện 12 dự án',
                startDate: '2020-09-04',
                endDate: '2022-06-02',
            },
        ],
    };

    return (
        <div>
            <Row>
                <Col
                    span={8}
                    style={{
                        display: 'flex',
                        justifyContent: 'center',
                        alignItems: 'center',
                    }}
                >
                    <Image
                        style={{ width: 150, height: 150, borderRadius: '50%' }}
                        src="https://zos.alipayobjects.com/rmsportal/jkjgkEfvpUPVyRjUImniVslZfWPnJuuZ.png"
                    />
                </Col>

                <Col
                    span={16}
                    style={{
                        display: 'flex',
                        flexDirection: 'column',
                        justifyContent: 'center',
                        paddingLeft: 20,
                    }}
                >
                    <h3>{employeeInfo?.employeeInfo?.fullName}</h3>
                    <p style={{ fontSize: '16px', fontStyle: 'italic' }}>
                        {employeeInfo?.employeeInfo?.registerContent
                            ? employeeInfo?.employeeInfo?.registerContent
                            : 'Thực tập sinh Front-end'}
                    </p>
                    <p>{dataCV.hobby}</p>
                </Col>
            </Row>

            <Row style={{ backgroundColor: 'rgba(34, 42, 69, 0.9)', color: '#fff', margin: '10px 0' }}>
                <Col style={{ textAlign: 'left', padding: '5px 5px' }} span={8}>
                    <GoogleSquareFilled /> {employeeInfo?.employeeInfo?.email}
                </Col>
                <Col style={{ textAlign: 'center', padding: '5px 5px' }} span={8}>
                    <PhoneFilled /> {employeeInfo?.employeeInfo?.phone}
                </Col>
                <Col style={{ textAlign: 'center', padding: '5px 5px' }} span={8}>
                    <EnvironmentFilled />{' '}
                    {employeeInfo?.employeeInfo?.address ? employeeInfo?.employeeInfo?.address : 'Hà Nội'}
                </Col>
            </Row>

            <div>
                <h4>Mục tiêu</h4>
                <p>{dataCV.careerGoal}</p>
            </div>

            <div>
                <h4>Các kỹ năng</h4>
                <p>{dataCV.skill}</p>
            </div>

            <div>
                <h4>Kinh nghiệm làm việc</h4>
                <div>
                    {dataCV.workExperiences.map((item, index) => (
                        <div key={index}>
                            <p>
                                <span style={{ display: 'inline-block', width: '30%' }}>
                                    {' '}
                                    - Công ty: {item.company}
                                </span>
                                <span style={{ display: 'inline-block', width: '30%' }}>Vị trí: {item.position}</span>
                                <span style={{ display: 'inline-block', width: '30%' }}>Mô tả: {item.detail}</span>
                            </p>
                            <p>
                                <span style={{ display: 'inline-block', width: '30%', whiteSpace: 'pre-wrap' }}>
                                    {' '}
                                    Ngày bắt đầu: {item.startDate}
                                </span>
                                <span style={{ display: 'inline-block', width: '30%' }}>
                                    Ngày kết thúc: {item.endDate}
                                </span>
                            </p>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
};

export default EndStaffProfile;
