import { EyeFilled, HomeFilled } from '@ant-design/icons';
import { Breadcrumb, Table, Tooltip } from 'antd';
import React from 'react';
import { useEffect } from 'react';
import { useState } from 'react';
import { ListStatus } from '../general-settings/setting';
import { getListEndStaff } from './EndStaffService';
import ModalFormInformation from './ModalFormInformation';
import ModalSaveRecords from './ModalSaveRecords';

const EndStaff = () => {
    const [employeeId, setEmployeeId] = useState({});
    const [listEndStaffData, setListEndStaffData] = useState([]);
    const [informationForm, setInformationForm] = useState(false);
    const [openModalSaveRecords, setOpenModalSaveRecords] = useState(false);

    const handleGetListNewStaff = async () => {
        const res = await getListEndStaff()
            .then((response) => {
                setListEndStaffData(response.data.data);
            })
            .catch((error) => {
                console.log('error', error);
            });
    };

    useEffect(() => {
        handleGetListNewStaff();
    }, []);

    console.log(listEndStaffData)

    const handleShowFormInformation = () => {
        setInformationForm(!informationForm);
    };

    const ViewEndStaffButton = ({ item, onSelect }) => {
        return (
            <div style={{ display: 'flex', alignItems: 'center' }}>
                <Tooltip placement="bottom" title="view record">
                    <EyeFilled style={{ color: 'rgb(40, 153, 40)', fontSize: '20px' }} onClick={() => onSelect(item)} />
                </Tooltip>
            </div>
        );
    };

    const handleOpenModalSaveRecords = () => {
        setOpenModalSaveRecords(!openModalSaveRecords)
    };

    return (
        <div className="m-sm-30 approved_staff">
            <Breadcrumb style={{ padding: '0 0 30px 0' }}>
                <Breadcrumb.Item href="/">
                    <HomeFilled />
                </Breadcrumb.Item>
                <Breadcrumb.Item href="/endStaff/manage">
                    <span>Quản lý nhân viên</span>
                </Breadcrumb.Item>
                <Breadcrumb.Item>Kết thúc</Breadcrumb.Item>
            </Breadcrumb>

            <Table
                columns={[
                    {
                        key: 'action',
                        title: 'Hành động',
                        render: (record) => {
                            return (
                                <ViewEndStaffButton
                                    item={record}
                                    onSelect={(record) => {
                                        handleShowFormInformation();
                                        setEmployeeId(record)
                                    }}
                                />
                            );
                        },
                    },
                    { key: 'code', title: 'Mã nhân viên', dataIndex: 'code' },
                    { key: 'fullName', title: 'Họ và tên', dataIndex: 'fullName' },
                    { key: 'terminatedDate', title: 'Ngày nộp lưu', dataIndex: 'terminatedDate' },
                    { key: 'storedProfileCode', title: 'Số lưu', dataIndex: 'storedProfileCode' },
                    {
                        key: 'status',
                        title: 'Trạng thái',
                        render: (record) => <ListStatus record={record} />
                    },
                ]}
                dataSource={listEndStaffData}
                pagination={{
                    defaultPageSize: 10,
                    showSizeChanger: true,
                    pageSizeOptions: ['5', '10', '20', '30'],
                }}
            />

            {
                informationForm && <ModalFormInformation
                    employeeId={employeeId}
                    open={informationForm}
                    onOk={handleOpenModalSaveRecords}
                    onCancel={handleShowFormInformation}
                />
            }
            {
                openModalSaveRecords && <ModalSaveRecords
                    employeeId={employeeId}
                    open={openModalSaveRecords}
                    onCancel={handleOpenModalSaveRecords}
                />
            }
        </div>
    );
};

export default EndStaff;
