import {
    EnvironmentFilled, GoogleSquareFilled,
    PhoneFilled, DeleteFilled, EditFilled
} from "@ant-design/icons";
import {
    Button, Col, Form, Image, Input,
    Row, Tooltip, Table, Modal
} from "antd";
import React, { useState } from "react";
import Styled from './Styled';
import moment from "moment";

const Columns = (handleClickDelete, handleClickEdit) => [
    {
        title: 'Hành động',
        dataIndex: '',
        width: 110,
        align: "center",
        render: (rowData) => (
            <div style={{ display: "flex", justifyContent: "center", gap: "0 25px" }}>
                <Tooltip
                    title="Sửa"
                    type="primary"
                    placement="bottom"
                    onClick={() => handleClickEdit(rowData)}
                >
                    <EditFilled style={{ color: "blue" }} />
                </Tooltip>

                <Tooltip
                    title="Xóa"
                    type="primary"
                    placement="bottom"
                    onClick={() => handleClickDelete(rowData)}
                >
                    <DeleteFilled style={{ color: "red" }} />
                </Tooltip>
            </div>
        )
    },
    {
        title: 'Công ty',
        dataIndex: 'company',
    },
    {
        title: 'Vị trí',
        dataIndex: 'position',
    },
    {
        title: 'Mô tả',
        dataIndex: 'detail',
    },
    {
        title: 'Ngày bắt đầu',
        render: (rowData) => moment(rowData.startDate, 'YYYY-MM-DD').format('DD-MM-YYYY')
    },
    {
        title: 'Ngày kết thúc',
        render: (rowData) => moment(rowData.endDate, 'YYYY-MM-DD').format('DD-MM-YYYY')
    }
];

const DialogDelete = (props) => {

    const { open, onOk, onCancel } = props

    return (
        <Modal
            title="Bạn có chắc chắn muốn xóa ?"
            open={open}
            onOk={onOk}
            onCancel={onCancel}
            okText="Xác nhận"
            cancelText="Hủy"
        />
    )
}

const StaffProfile = ({ staffInfo, workExperiencesList, setWorkExperiencesList }) => {
    const employeeInfo = staffInfo.employeeInfo

    const [form] = Form.useForm();
    const [shouldOpenDialogDelete, setShouldOpenDialogDelete] = useState()
    const [rowDelete, setRowDelete] = useState()
    const [rowEdit, setRowEdit] = useState()

    const uniqueId = () => {
        let id = "" + Math.random().toString(36).substring(2, 4) +
            Date.now().toString(36).substring(2, 4)
        return id
    };

    // ======Thêm =========== >>>>>>>>>
    const onFinish = (values) => {
        form.resetFields()
        if (rowEdit) {
            const newValue = {
                ...values,
                id: rowEdit.id,
            }

            const index = workExperiencesList.findIndex((item) => item.id === rowEdit.id)
            const newArr = [...workExperiencesList]
            newArr[index] = { ...newArr[index], ...newValue }

            setWorkExperiencesList(newArr)
            setRowEdit()
            return
        }
        values = {
            ...values,
            id: uniqueId(),
        }
        setWorkExperiencesList([...workExperiencesList, values])
    }
    // ===================== <<<<<<<<<<

    // ======== Xóa ======== >>>>>>>>>>
    const handleClickDelete = (rowData) => {
        setRowDelete(rowData)
        setShouldOpenDialogDelete(true)
    }

    const onOk = () => {
        const newList = workExperiencesList.filter((item) => item.id !== rowDelete.id)
        if (rowDelete.id === rowEdit?.id) {
            form.resetFields()
            setRowEdit()
        }
        setRowDelete()
        setWorkExperiencesList(newList)
        setShouldOpenDialogDelete(false)
    }

    const onCancel = () => {
        setRowDelete()
        setShouldOpenDialogDelete(false)
    }
    // ===================== <<<<<<<<<<

    // ========= Sửa ======= >>>>>>>>>>
    const handleClickEdit = (rowData) => {
        form.setFieldsValue({
            company: rowData.company,
            position: rowData.position,
            detail: rowData.detail,
            startDate: rowData.startDate,
            endDate: rowData.endDate
        });
        setRowEdit(rowData)
    }
    // ===================== <<<<<<<<<<

    const teamId = () => {
        if (employeeInfo?.teamId === 1) {
            return "Back-end"
        } else if (employeeInfo?.teamId === 2) {
            return "Front-end"
        } else if (employeeInfo?.teamId === 3) {
            return "Tester"
        } else {
            return
        }
    }

    const columns = Columns(handleClickDelete, handleClickEdit)

    return (
        <Styled>
            <div style={{ padding: '0 20px' }}>
                <Row>
                    <Col span={8} style={{
                        display: "flex",
                        justifyContent: "center", alignItems: "center"
                    }}>
                        <Image
                            style={{ width: 150, height: 150, borderRadius: "50%" }}
                            src={employeeInfo?.photoUrl ? employeeInfo?.photoUrl : "https://zos.alipayobjects.com/rmsportal/jkjgkEfvpUPVyRjUImniVslZfWPnJuuZ.png"}
                        />
                    </Col>

                    <Col span={16} style={{
                        display: "flex",
                        flexDirection: "column", justifyContent: "center", paddingLeft: 20
                    }}>
                        <h3>{employeeInfo?.fullName}</h3>
                        <p style={{ fontSize: "16px", fontStyle: "italic" }}>{teamId()}</p>
                        <Form.Item name="hobby">
                            <Input style={{ width: '60%' }} />
                        </Form.Item>
                    </Col>
                </Row>
                <Row style={{ backgroundColor: "#289928", color: "#fff", margin: "10px 0" }}>
                    <Col style={{ textAlign: "left", padding: "5px 5px" }} span={8}><GoogleSquareFilled /> {employeeInfo?.email}</Col>
                    <Col style={{ textAlign: "center", padding: "5px 5px" }} span={8}><PhoneFilled /> {employeeInfo?.phone}</Col>
                    <Col style={{ textAlign: "center", padding: "5px 5px" }} span={8}><EnvironmentFilled /> {employeeInfo?.address}</Col>
                </Row>

                <div>
                    <h4>Mục tiêu</h4>
                    <Form.Item name="careerGoal">
                        <Input />
                    </Form.Item>
                </div>

                <div>
                    <h4>Các kỹ năng</h4>
                    <Form.Item name="skill">
                        <Input />
                    </Form.Item>
                </div>

                {shouldOpenDialogDelete && (
                    <DialogDelete
                        open={shouldOpenDialogDelete}
                        onOk={onOk}
                        onCancel={onCancel}
                    />
                )}

                <div>
                    <h4>Kinh nghiệm làm việc</h4>
                    {/* <Button>Thêm mới</Button> */}
                    <Form
                        form={form}
                        onFinish={onFinish}
                    >
                        <Form.Item label="Công ty: " name="company" >
                            <Input style={{ marginLeft: 20 }} />
                        </Form.Item>
                        <Form.Item label="Chức vụ: " name="position" >
                            <Input style={{ marginLeft: 20 }} />
                        </Form.Item>
                        <Form.Item label="Mô tả: " name="detail">
                            <Input style={{ marginLeft: 20 }} />
                        </Form.Item>
                        <Form.Item label="Ngày bắt đầu: " name="startDate">
                            <Input
                                style={{ marginLeft: 20, width: 200 }}
                                type="date"
                            />
                        </Form.Item>
                        <Form.Item label="Ngày kết thúc" name="endDate">
                            <Input
                                style={{ marginLeft: 20, width: 200 }}
                                type="date"
                            />
                        </Form.Item>
                        <div>
                            <Button
                                className="btn-save"
                                htmlType="submit"
                            // onClick={handleSubmit}
                            >
                                LƯU
                            </Button>
                        </div>
                    </Form>

                    <Table
                        style={{ marginTop: 20 }}
                        dataSource={workExperiencesList}
                        columns={columns}
                        pagination={null}
                        scroll={{ y: 280, }}
                    />
                </div>
            </div>

        </Styled>
    )
}

export default StaffProfile;
