import React, { Component, useState, useEffect } from "react";
import { toast } from "react-toastify";
import { useTranslation } from "react-i18next";
import i18n from "i18n"
import {
    Row, Input, Breadcrumb, Table, Tooltip
} from "antd"
import { EyeFilled, HomeFilled } from "@ant-design/icons"
import StaffAcceptedDialog from "./StaffAcceptedDialog"
import { HeaderStyled } from "./StaffAcceptedStyled"
import { getByStatus, getTotalByStatus } from "./StaffAcceptedService";


const Columns = (openStaffAcceptedDialog) => [
    {
        title: 'Hành động',
        dataIndex: '',
        width: 110,
        align: "center",
        render: (rowData) => (
            <Tooltip
                title="Chi tiết"
                type="primary"
                size="small"
                placement="bottom"
                onClick={() => openStaffAcceptedDialog(rowData)}
            >
                <EyeFilled style={{ color: "#289928" }} />
            </Tooltip>
        )
    },
    {
        title: 'Họ Tên',
        dataIndex: 'fullName',
    },
    {
        title: 'Email',
        dataIndex: 'email',
    },
    {
        title: 'Số điện thoại',
        dataIndex: 'phone',
    },
    {
        title: 'Trạng thái',
        render: (rowData) => {
            if (rowData.status === 5) {
                return "Duyệt"
            } else if (rowData.status === 6) {
                return "Từ chối"
            } else {
                return "Lỗi"
            }
        }
    }
];

function StaffAccepted() {

    const [listStaffAccepted, setListStaffAccepted] = useState([])
    const [shouldOpenStaffAcceptedDialog, setShouldOpenStaffAcceptedDialog] = useState(false)
    const [item, setItem] = useState()

    const openStaffAcceptedDialog = (rowData) => {
        setItem(rowData)
        setShouldOpenStaffAcceptedDialog(true)
    }

    const onCancelStaffAcceptedDialog = () => {
        setShouldOpenStaffAcceptedDialog(false)
    }

    const columns = Columns(openStaffAcceptedDialog)

    useEffect(() => {
        const getListPending = async () => {
            let page = 0
            let list = []
            const resTotal = await getTotalByStatus([5, 6])

            do {
                ++page

                const res = await getByStatus({
                    arr: [5, 6],
                    page: page
                })

                list = list.concat(res.data.data)
            } while (list.length < resTotal.data.data)

            const newList = list.map((item, index) => {
                return {
                    ...item,
                    key: index
                }
            })

            setListStaffAccepted(newList)

        }
        getListPending()
    }, [])

    return (
        // <div className="m-sm-30">
        //     <Breadcrumb style={{ padding: '0 0 30px 0' }}>
        //         <Breadcrumb.Item href="/">
        //             <HomeFilled />
        //         </Breadcrumb.Item>
        //         <Breadcrumb.Item href="/conclude/manage">
        //             <span>Quản lý nhân viên</span>
        //         </Breadcrumb.Item>
        //         <Breadcrumb.Item>Tạo mới</Breadcrumb.Item>
        //     </Breadcrumb>

        //     <Row justify="end" style={{ marginBottom: 20 }}>
        //         <Input.Search style={{ width: 300 }} placeholder="Search"></Input.Search>
        //     </Row>

        //     <div>
        //         {shouldOpenStaffAcceptedDialog && (
        //             <StaffAcceptedDialog
        //                 open={shouldOpenStaffAcceptedDialog}
        //                 // onOk={onOk}
        //                 onCancel={onCancelStaffAcceptedDialog}
        //                 item={item}
        //             />
        //         )}
        //     </div>

        //     <HeaderStyled>
        //         <Table
        //             columns={columns}
        //             dataSource={listStaffAccepted}
        //             pagination={{ defaultPageSize: 10 }}
        //             scroll={{ y: 350 }}
        //         />
        //     </HeaderStyled>

        // </div>
        <div>hello</div>
    )
}

export default StaffAccepted
