import React from 'react';
import { Modal, Tabs } from 'antd';
import EndStaffProfile from './EndStaffProfile';
import CurriculumVitae from '../general-settings/CurriculumVitae';

function ModalFormInformation({ open, onOk, onCancel, employeeId }) {
    return (
        <Modal
            className="modal-information-end-staff modal_profile"
            centered
            okText="Nộp lưu hồ sơ"
            title="Thông tin nhân viên"
            cancelText="HỦY"
            open={open}
            onOk={onOk}
            onCancel={onCancel}
            width={'70vw'}
        >
            <Tabs
                defaultActiveKey="1"
                tabPosition="left"
                tabBarStyle={{ width: 180, whiteSpace: 'break-spaces' }}
                items={[
                    {
                        label: 'Hồ sơ',
                        key: 2,
                        children: <EndStaffProfile employeeId={employeeId} />,
                    },
                    {
                        label: 'Sơ yếu lí lịch',
                        key: 1,
                        children: <CurriculumVitae staffInfo={employeeId} />,
                    },
                ]}
            />
        </Modal>
    );
}

export default ModalFormInformation;
