import React, { useState, useEffect } from "react";
import { getTinTucAll } from "./TinTucService"
import Styled from '../create-staff/CreateStaffStyled';

import {
    DeleteFilled,
    EditFilled,
    EyeFilled,
    HomeFilled,
} from '@ant-design/icons';
import { Breadcrumb, Button, Form, Modal, Table, Tooltip } from 'antd';
import SearchStaff from '../create-staff/Search';
import { toast } from 'react-toastify';
import moment from "moment";
import TinTucDialog from "./TinTucDialog"

function ChiTietTour() {

    const [data, setData] = useState([])

    const [openDialogAdd, setOpenDialogAdd] = useState(false)

    const [cellVisible, setCellVisible] = useState({});
    const handleCellClick = (key) => {
        setCellVisible({
            ...cellVisible,
            [key]: !cellVisible[key]
        });
    };

    // === get =====
    const getData = async () => {
        const res = await getTinTucAll()
        console.log("==> res", res.data)
        setData(res.data)
    }
    // =============

    // ======= them ========

    const handleClickAdd = () => {
        // setTourEdit()
        setOpenDialogAdd(true)
    }

    const cancelDialogAdd = async () => {
        setOpenDialogAdd(false)
        setCellVisible({})
        // setTourEdit()
        getData()
    }

    // =====================

    useEffect(() => {
        getData()
    }, [])

    return (
        <Styled>
            <div className="m-sm-30">
                <Breadcrumb style={{ padding: '0 0 30px 0' }}>
                    <Breadcrumb.Item href="/">
                        <HomeFilled />
                    </Breadcrumb.Item>
                    <Breadcrumb.Item href="/conclude/manage">
                        <span>Quản lý nhân viên</span>
                    </Breadcrumb.Item>
                    <Breadcrumb.Item>Tạo mới</Breadcrumb.Item>
                </Breadcrumb>

                <div className='set-space-between'>
                    <Button size="middle"
                        onClick={handleClickAdd}
                        className="btn-add-new"
                    >
                        TẠO MỚI
                    </Button>
                    <SearchStaff
                    // searchInfo={searchInfo} setSearchInfo={setSearchInfo} onSearch={onSearch}
                    />
                </div>

                {
                    openDialogAdd &&
                    <TinTucDialog
                        // item={tourEdit}
                        open={openDialogAdd}
                        onCancel={cancelDialogAdd}
                    />
                }

                {/* {
                openDialogDelete &&
                <DialogDelete
                    open={openDialogDelete}
                    onOk={onOkDelete}
                    onCancel={onCancelDelete}
                />
            } */}


                <Table
                    dataSource={data}
                    columns={[
                        {
                            key: 'action',
                            title: 'Thao tác',
                            dataIndex: 'action',
                            align: 'center',
                            width: 100,
                            render: (text, rowData) => {
                                return (
                                    <div
                                        className='createStaff-action'
                                        style={{ gap: "16px 16px" }}
                                    >
                                        <Tooltip placement="bottom" title="chỉnh sửa">
                                            <EditFilled
                                                style={{ color: "blue" }}
                                            // onClick={() => onClickIconEdit(rowData)}
                                            />
                                        </Tooltip>

                                        <Tooltip placement="bottom" title="xóa">
                                            <DeleteFilled
                                                style={{ color: "red" }}
                                            // onClick={() => onClickIconDelete(rowData)}
                                            />
                                        </Tooltip>
                                    </div>
                                );
                            },
                        },
                        { title: 'Id', dataIndex: 'id', width: 50 },
                        {
                            title: 'Tieu de',
                            dataIndex: "tieuDe"
                        },
                        {
                            title: 'Noi dung',
                            dataIndex: "noiDung",
                            render: (text, record) => {
                                return (
                                    <div onClick={() => handleCellClick(record.key)}>
                                        {cellVisible[record.key] ? text : record.noiDung.substring(0, 50) + "..."}
                                    </div>
                                );
                            }
                        },
                        {
                            title: 'Anh mot',
                            width: 120,
                            render: rowData => <img style={{ width: 100 }} src={rowData?.anhMot && `http://127.0.0.1:8887/${rowData.anhMot}`} alt="ing" />
                        },
                        {
                            title: 'Anh hai',
                            width: 120,
                            render: rowData => <img style={{ width: 100 }} src={rowData?.anhHai && `http://127.0.0.1:8887/${rowData.anhHai}`} alt="ing" />
                        },
                    ]}
                    pagination={{
                        showSizeChanger: true,
                        pageSizeOptions: ['5', '10', '20'],
                    }}
                    scroll={{ y: 440 }}
                />
            </div>
        </Styled>
    )
}

export default ChiTietTour
