import React, { useEffect, useState } from "react";
import { Modal, Button, Tabs, Row, Col, Table, Image, Form, Input, Upload } from "antd"

import styled from 'styled-components';
import moment from "moment";
import { addTinTuc } from "./TinTucService";


const beforeUpload = (file) => {
    const isJpgOrPng = file.type === "image/jpeg" || file.type === "image/png" || file.type === "image/jpg";
    if (!isJpgOrPng) {
        alert("You can only upload JPG/PNG file!");
    }
    return isJpgOrPng;
}

function TinTucDialog(props) {

    const { open, onCancel, item } = props
    const [form] = Form.useForm()

    const [fileImage, setFileImage] = useState()
    const [fileImage2, setFileImage2] = useState()
    const handleUpload = (event) => {
        console.log('Tên tệp tin:', event.file.name);
        setFileImage(event.file.name)
    }

    const handleUpload2 = (event) => {
        console.log('Tên tệp tin:', event.file.name);
        setFileImage2(event.file.name)
    }

    const submitForm = async (values) => {


        const newValue = {
            ...values,
            anhMot: fileImage,
            anhHai: fileImage2
        }
        console.log("==>", newValue)

        if (item) {
            // const res = await updateTourChiTiet(item.idChiTiet, newValue)
            // if (res.status === 200) {
            //     toast.success("Sua thanh cong")
            // } else {
            //     toast.error("Loi roi !!!!!!!!!!!!!!")
            // }
        } else {
            const res = await addTinTuc(newValue)
            if (res.status === 200) {
                toast.success("Them hanh cong")
            } else {
                toast.error("Loi roi !!!!!!!!!!!!!!")
            }
        }
        form.resetFields()
        onCancel()
    }

    return (
        <Modal
            title="Them Tour"
            open={open}
            width={1000}
            style={{ height: 700 }}
            // onOk={handleOk} 
            onCancel={onCancel}
            footer={null}
        >
            <Form
                form={form}
                onFinish={submitForm}
                layout="vertical"
            >
                <Row gutter={[16, 16]}>

                    <Col span={24}>
                        <Form.Item
                            name="tieuDe"
                            label="Tieu de"
                            rules={[
                                {
                                    required: true,
                                    message: "Bat buoc phai nhap"
                                },
                            ]}
                        >
                            <Input />
                        </Form.Item>
                    </Col>
                </Row>

                <Row>
                    <Col span={24}>
                        <Form.Item
                            name="noiDung"
                            label="Noi dung"
                            rules={[
                                {
                                    required: true,
                                    message: "Bat buoc phai nhap"
                                },
                            ]}
                        >
                            <Input.TextArea />
                        </Form.Item>
                    </Col>
                </Row>

                <Row gutter={[0, 24]}>
                    <Col span={8} className="image-info">
                        <Row>
                            <div className="avatar" >
                                <Image
                                    // src={fileImage ? fileImage?.toString() : "error"}
                                    src={fileImage && `http://127.0.0.1:8887/${fileImage}`}
                                    fallback="data:image/png;base64,"
                                />
                                {/* <img src={data?.anhMot && `http://127.0.0.1:8887/${data.anhMot}`} alt="ing" /> */}
                            </div>
                        </Row>
                        <Row >
                            <Form.Item name="anhMot">
                                <Upload
                                    name="anhMot"
                                    action="https://www.mocky.io/v2/5cc8019d300000980a055e76"
                                    onChange={handleUpload}
                                    beforeUpload={beforeUpload}
                                    showUploadList={false}
                                >
                                    <Button >
                                        Thêm ảnh đại diện
                                    </Button>
                                </Upload>
                            </Form.Item>
                        </Row>
                    </Col>

                    <Col span={8} className="image-info">
                        <Row>
                            <div className="avatar" >
                                <Image
                                    // src={fileImage ? fileImage?.toString() : "error"}
                                    src={fileImage && `http://127.0.0.1:8887/${fileImage2}`}
                                    fallback="data:image/png;base64,"
                                />
                                {/* <img src={data?.anhMot && `http://127.0.0.1:8887/${data.anhMot}`} alt="ing" /> */}
                            </div>
                        </Row>
                        <Row >
                            <Form.Item name="anhHai">
                                <Upload
                                    name="anhHai"
                                    action="https://www.mocky.io/v2/5cc8019d300000980a055e76"
                                    onChange={handleUpload2}
                                    beforeUpload={beforeUpload}
                                    showUploadList={false}
                                >
                                    <Button >
                                        Thêm ảnh đại diện
                                    </Button>
                                </Upload>
                            </Form.Item>
                        </Row>
                    </Col>
                </Row>

                <Row justify={"center"}>
                    <Form.Item>
                        <Button type="primary" htmlType="submit">
                            Submit
                        </Button>
                    </Form.Item>
                </Row>
            </Form>
        </Modal>
    )
}

export default TinTucDialog
