import { Modal } from "antd"
import React from "react"
import { deleteStaff } from "../CreateStaffService"
import { toast } from "react-toastify"
import styled from "styled-components"

const DialogDeleteStaff = ({ open, handleClose, employeeId }) => {

    const handleDelete = async () => {
        const res = await deleteStaff({ employeeId })

        console.log("res", res);
        if (res?.status === 200) {
            handleClose();
            toast.success("Xóa thành công")
        } else {
            toast.warning(res?.data?.message)
        }
    }

    return (
        <StyledModal
            title="Xóa nhân viên"
            centered
            open={open}
            width={500}
            okText="Xác nhận"
            cancelText="Hủy"
            onCancel={handleClose}
            onOk={handleDelete}
            className="modal-delete"
        >
            <p>Bạn có chắc chắn muốn xóa?</p>
        </StyledModal>
    )
}

export default DialogDeleteStaff

const StyledModal = styled(Modal)`
    p {
        margin: 0;
    }

    .ant-btn-primary {
        background-color: #0d5ac3;
    }

    .ant-btn-primary:hover {
        background-color: #3365a9 !important;
    }

    .ant-modal-header {
        box-shadow: none;
    }

    .ant-modal-footer {
        display: flex;
        justify-content: center;
        flex-direction: row-reverse;
        gap: 10px;
        box-shadow: none;
        margin: 0
    }
`
