import { Modal } from "antd"
import React from "react"
import styled from "styled-components"

const DialogDeleteText = (props) => {

    const { open, handleCloseDialogDeleteText, listDiploma, setListDiploma, diplomaId, selectIndex } = props;

    const handleDelete = () => {
        if (diplomaId) {
            setListDiploma(listDiploma.filter((item) => item?.certificateId.toString() !== diplomaId.toString()))
        } else {
            setListDiploma(listDiploma.filter((item) => item?.index.toString() !== selectIndex.toString()));
        }
        handleCloseDialogDeleteText();
    }
    console.log("listDiploma", listDiploma);

    return (
        <>
            <StyledModal
                title="XÓA VĂN BẰNG"
                centered
                open={open}
                width={500}
                onCancel={handleCloseDialogDeleteText}
                okText="Xác nhận"
                cancelText="Hủy"
                onOk={handleDelete}
            >
                <p>Bạn có chắc chắn muốn xóa?</p>
            </StyledModal>
        </>
    )
}

export default DialogDeleteText

const StyledModal = styled(Modal)`
    p {
        margin: 0;
    }

    .ant-btn-primary {
        background-color: #0d5ac3;
    }

    .ant-btn-primary:hover {
        background-color: #3365a9 !important;
    }

    .ant-modal-header {
        box-shadow: none;
    }

    .ant-modal-footer {
        display: flex;
        justify-content: center;
        flex-direction: row-reverse;
        gap: 10px;
        box-shadow: none;
        margin: 0
    }
`
