        //             columns={[
        //                 {
        //                     key: 'action',
        //                     title: 'Thao tác',
        //                     dataIndex: 'action',
        //                     align: 'center',
        //                     width: 100,
        //                     render: (text, rowData) => {
        //                         return (
        //                             <div
        //                                 className='createStaff-action'
        //                                 style={{ gap: "16px 16px" }}
        //                             >
        //                                 <Tooltip placement="bottom" title="chỉnh sửa">
        //                                     <EditFilled
        //                                         style={{ color: "blue" }}
        //                                         onClick={() => onClickIconEdit(rowData)}
        //                                     />
        //                                 </Tooltip>

        //                                 <Tooltip placement="bottom" title="xóa">
        //                                     <DeleteFilled
        //                                         style={{ color: "red" }}
        //                                         onClick={() => onClickIconDelete(rowData)}
        //                                     />
        //                                 </Tooltip>
        //                             </div>
        //                         );
        //                     },
