        //                 {
        //                     title: 'HInh anh',
        //                     // dataIndex: "hinhAnh"
        //                     render: rowData => rowData.hinhAnh.substring(0, 30) + "..."
        //                 }
        //             ]}
        //             pagination={{
        //                 showSizeChanger: true,
        //                 pageSizeOptions: ['5', '10', '20'],
        //             }}
        //             scroll={{ y: 440 }}
        //         />
        //     </div>
        // </Styled>
        <div>hello</div>
    )
}

