import { DeleteFilled, EditFilled } from "@ant-design/icons";
import { Button, Col, Form, Input, Row, Select, Table, Tooltip } from "antd";
import { Option } from "antd/es/mentions";
import React, { useState } from "react";
import { useTranslation } from "react-i18next";
import Styled from "./styled";
import moment from "moment";
import DialogDeleteFamilyRelationship from "./dialog-delete-family-relationship";

const TabFamilyRelationShip = (props) => {
    const { listRelationship, setListRelationship, listGender } = props;
    const [selectIndex, setSelectIndex] = useState();
    const [relationshipId, setRelationshipId] = useState(0);
    const [shouldOpenDialogDeleteText, setShouldOpenDialogDeleteText] = useState(false)

    const { t } = useTranslation();
    const [form] = Form.useForm();

    const onFinish = (value) => {

        if (typeof selectIndex === "number") {
            if (!listRelationship?.familyId) {
                const dataUpdate = listRelationship.map((item, index) => {
                    const itemUpdate = index === selectIndex ? { ...item, ...value } : item
                    return {
                        ...itemUpdate,
                    };
                });
                setListRelationship([...dataUpdate]);
            } else {
                console.log("aaaaa");
            }

        }
        else {
            const newItemWithIndex = { ...value, index: listRelationship.length.toString() };
            setListRelationship([
                ...listRelationship,
                newItemWithIndex
            ]);
        }

        form.resetFields();
        setSelectIndex("")
    };


    const columns = [
        {
            key: "action",
            title: t("general.action"),
            dataIndex: "action",

            width: "10%",

            render: (rowDataFamilyRelationship, recordFamilyRelationship, index) => {
                return (
                    <div style={{ display: "flex" }} >
                        <Tooltip placement="bottom" title="chỉnh sửa">
                            <EditFilled
                                style={{ color: "#0d4ca5" }}
                                onClick={() => onClickEditFamilyRelationship(recordFamilyRelationship, index)}
                            />
                        </Tooltip>
                        <Tooltip placement="bottom" title="xóa">
                            <DeleteFilled
                                style={{ color: "red" }}
                                onClick={() => onClickDeleteFamilyRelationship(index, recordFamilyRelationship)}
                            />
                        </Tooltip>
                    </div>
                )
            }
        },
        { key: "name", title: "Họ và tên", dataIndex: "name", width: "17%" },
        {
            key: "dateOfBirth", title: "Ngày sinh", dataIndex: "dateOfBirth", width: "14%",
            render: (rowData) => {
                return (
                    <>{moment(rowData).format("DD-MM-YYYY")}</>
                )
            }
        },
        {
            key: "gender", title: "Giới tính", dataIndex: "gender", width: "12%",
            render: (gender) => {
                const labelGender = listGender.find((item) => item.value == gender)

                return (
                    <>{labelGender.label}</>
                )
            }
        },
        { key: "relation", title: "Quan hệ", dataIndex: "relation", width: "12%" },
        { key: "citizenId", title: "Số CCCD/CMT", dataIndex: "citizenId", width: "15%" },
        { key: "address", title: "Địa chỉ", dataIndex: "address", width: "20%" },
    ];

    const onClickEditFamilyRelationship = (record, index) => {

        form.setFieldsValue({
            name: record?.name,
            dateOfBirth: record?.dateOfBirth?.split(" ")[0],
            gender: record?.gender,
            relation: record?.relation,
            citizenId: record?.citizenId,
            address: record?.address
        });
        setSelectIndex(index);
    }


    const onClickDeleteFamilyRelationship = (index, recordFamilyRelationship) => {
        setRelationshipId(recordFamilyRelationship?.familyId)
        setShouldOpenDialogDeleteText(true)
        setSelectIndex(index)
        form.resetFields();
    }

    const handleCloseDialogDeleteFamilyRelationship = () => {
        setShouldOpenDialogDeleteText(false)
    }

    return (
        <Styled>
            {shouldOpenDialogDeleteText && (
                <DialogDeleteFamilyRelationship
                    open={shouldOpenDialogDeleteText}
                    handleCloseDialogDeleteFamilyRelationship={handleCloseDialogDeleteFamilyRelationship}
                    listRelationship={listRelationship}
                    setListRelationship={setListRelationship}
                    selectIndex={selectIndex}
                    relationshipId={relationshipId}
                />
            )}

            <Form
                wrapperCol={{
                    span: 24,
                }}
                onFinish={onFinish}
                layout="vertical"
                form={form}
            >
                <Col span={24} style={{ boxSizing: "border-box" }}>
                    <Row justify="space-between" >
                        <Col className="gutter-row" span={6}>
                            <Form.Item
                                label='Họ và tên người thân'
                                name="name"
                                rules={[
                                    {
                                        required: true,
                                        message: 'Bắt buộc phải nhập trường này!',
                                    },
                                    { whitespace: true, message: 'không được để trống' },
                                ]}
                            >
                                <Input style={{ width: "100%" }} />
                            </Form.Item>
                        </Col>
                        <Col className="gutter-row" span={5}>
                            <Form.Item
                                label='Số CCCD/CMT'
                                name="citizenId"
                                rules={[
                                    {
                                        required: true,
                                        message: 'Bắt buộc phải nhập trường này!',
                                    },
                                    {
                                        pattern: '^([-]?[0-9][0-9]*|0)$',
                                        message: 'Trường này phải là số',
                                    },
                                    { max: 12, message: 'Tối đa 12 kí tự', }
                                ]}
                            >
                                <Input style={{ width: "100%" }} />
                            </Form.Item>
                        </Col>
                        <Col className="gutter-row" span={5}>
                            <Form.Item
                                label="Ngày sinh"
                                name="dateOfBirth"
                                rules={[
                                    {
                                        required: true,
                                        message: 'Bắt buộc phải chọn trường này!',
                                    },
                                ]}
                            >
                                <Input type="date" style={{ width: "100%" }} />
                            </Form.Item>
                        </Col>
                        <Col className="gutter-row" span={4}>
                            <Form.Item
                                label='Quan hệ'
                                name="relation"
                                rules={[
                                    {
                                        required: true,
                                        message: 'Bắt buộc phải nhập trường này!',
                                    },
                                ]}
                            >
                                <Input style={{ width: "100%" }} />
                            </Form.Item>
                        </Col>
                        <Col className="gutter-row" span={4}>
                            <Form.Item
                                name="gender"
                                label="Giới tính"
                                rules={[
                                    {
                                        required: true,
                                        message: 'Bắt buộc phải chọn trường này!',
                                    },
                                ]}
                            >
                                <Select
                                    style={{ width: "100%" }}
                                    placeholder="Giới tính"
                                // onChange={onGenderChange}
                                >
                                    {listGender.map(item => {
                                        return <Option key={item.value} value={item.value}>{item.label}</Option>;
                                    })}
                                </Select>
                            </Form.Item>
                        </Col>
                        <Col className="gutter-row" span={16}>
                            <Form.Item
                                label='Địa chỉ cụ thể'
                                name="address"
                                rules={[
                                    {
                                        required: true,
                                        message: 'Bắt buộc phải nhập trường này!',
                                    },
                                    { whitespace: true, message: 'không được để khoảng trằng trống' },
                                ]}
                            >
                                <Input style={{ width: "100%" }} />
                            </Form.Item>
                        </Col>
                        <Col className="gutter-row btn-form" span={8}>
                            <Form.Item>
                                <div className="btn-family-relationship">
                                    <Button className="btn-add" htmlType="submit">
                                        Thêm
                                    </Button>
                                    <Button className="btn-reset" onClick={() => form.resetFields()}>
                                        đặt lại
                                    </Button>
                                </div>

                            </Form.Item>
                        </Col>
                        {/* <Col span={1}></Col> */}
                    </Row>
                </Col>

            </Form>

            <Table scroll={{ y: 250 }} rowKey="index" dataSource={listRelationship} columns={columns} pagination={false} ></Table>

        </Styled>
    )
}
