import React, { useEffect, useState } from "react";
import { Modal, Button, Tabs, Row, Col, Table, Image, Form, Input, Upload } from "antd"

import styled from 'styled-components';
import moment from "moment";

import { toast } from "react-toastify";
import { values } from "lodash";
import { addKhachDat, updateKhachDat } from "./KhachDatService";

toast.configure({
    autoClose: 1000,
    draggable: false,
    limit: 3,
    closeOnClick: true
});


const beforeUpload = (file) => {
    const isJpgOrPng = file.type === "image/jpeg" || file.type === "image/png" || file.type === "image/jpg";
    if (!isJpgOrPng) {
        alert("You can only upload JPG/PNG file!");
    }
    return isJpgOrPng;
}

function KhachSanDialog(props) {

    const { open, onCancel, item } = props
    const [form] = Form.useForm()

    // const [fileImage, setFileImage] = useState()
    // const handleUpload = (event) => {
    //     console.log('Tên tệp tin:', event.file.name);
    //     setFileImage(event.file.name)
    // }

    const submitForm = async (values) => {


        // const newValue = {
        //     ...values,
        //     hinhAnh: fileImage
        // }

        console.log("==>", values)

        // if (item) {
        //     const res = await updateKhachSan(item.idKhachSan, newValue)
        //     if (res.status === 200) {
        //         toast.success("Sua thanh cong")
        //     } else {
        //         toast.error("Loi roi !!!!!!!!!!!!!!")
        //     }
        // } else {
        //     const res = await addKhachSan(newValue)
        //     console.log("==> res: ", res)
        //     if (res.status === 200) {
        //         toast.success("Them hanh cong")
        //     } else {
        //         toast.error("Loi roi !!!!!!!!!!!!!!")
        //     }
        // }
        // form.resetFields()
        // onCancel()
    }

    // useEffect(() => {
    //     // console.log("==>", item)
    //     if (item) {
    //         form.setFieldsValue(item)
    //         setFileImage(item.hinhAnh)
    //     }
    // }, [])

    return (
        <Modal
            title="Them Tour"
            open={open}
            width={1000}
            style={{ height: 700 }}
            // onOk={handleOk} 
            onCancel={onCancel}
            footer={null}
        >
            <Form
                form={form}
                onFinish={submitForm}
                layout="vertical"
            >
                <Row gutter={[16, 16]}>

                    <Col span={6}>
                        <Form.Item
                            name="hoTen"
                            label="Ho Ten"
                            rules={[
                                {
                                    required: true,
                                    message: "Bat buoc phai nhap"
                                },
                            ]}
                        >
                            <Input />
                        </Form.Item>
                    </Col>

                    <Col span={6}>
                        <Form.Item
                            name="sdt"
                            label="sdt"
                            rules={[
                                {
                                    required: true,
                                    message: "Bat buoc phai nhap"
                                },
                            ]}
                        >
                            <Input />
                        </Form.Item>
                    </Col>

                    <Col span={6}>
                        <Form.Item
                            name="email"
                            label="email"
                            rules={[
                                {
                                    required: true,
                                    message: "Bat buoc phai nhap"
                                },
                            ]}
                        >
                            <Input />
                        </Form.Item>
                    </Col>
                </Row>

                <Row gutter={[16, 16]}>

                    <Col span={6}>
                        <Form.Item
                            name="diaChi"
                            label="diaChi"
                            rules={[
                                {
                                    required: true,
                                    message: "Bat buoc phai nhap"
                                },
                            ]}
                        >
                            <Input />
                        </Form.Item>
                    </Col>

                    <Col span={6}>
                        <Form.Item
                            name="yeuCau"
                            label="yeuCau"
                            rules={[
                                {
                                    required: true,
                                    message: "Bat buoc phai nhap"
                                },
                            ]}
                        >
                            <Input />
                        </Form.Item>
                    </Col>

                    <Col span={6}>
                        <Form.Item
                            name="idKS"
                            label="idKS"
                            rules={[
                                {
                                    required: true,
                                    message: "Bat buoc phai nhap"
                                },
                            ]}
                        >
                            <Input />
                        </Form.Item>
                    </Col>
                </Row>


                <Row justify={"center"}>
                    <Form.Item>
                        <Button type="primary" htmlType="submit">
                            Submit
                        </Button>
                    </Form.Item>
                </Row>
            </Form>
        </Modal>
    )
}

export default KhachSanDialog
