import React, { Component, useEffect, useState } from "react";
import { toast } from "react-toastify";
import { useTranslation } from "react-i18next";
import i18n from "i18n"
import {
    Row, Input, Breadcrumb, Table, Tooltip
} from "antd"
import { EyeFilled, HomeFilled } from "@ant-design/icons"
import PendingDialog from "./PendingDialog"
import { HeaderStyled } from "./PendingStyled"
import { getByStatus } from "./PendingService";



const Columns = (openPendingDialog) => [
    {
        title: 'Hành động',
        dataIndex: '',
        width: 110,
        align: "center",
        render: (rowData) => (
            <Tooltip
                title="Chi tiết"
                type="primary"
                size="small"
                placement="bottom"
                onClick={() => openPendingDialog(rowData)}
            >
                <EyeFilled style={{ color: "#289928" }} />
            </Tooltip>
        )
    },
    {
        title: 'Họ Tên',
        dataIndex: 'fullName',
    },
    {
        title: 'Email',
        dataIndex: 'email',
    },
    {
        title: 'Số điện thoại',
        dataIndex: 'phone',
    },
    // {
    //     title: 'Trạng thái',
    //     dataIndex: 'status',
    // }
];

function Pending() {

    const [listPending, setListPending] = useState([])
    const [shouldOpenPendingDialog, setShouldOpenPendingDialog] = useState(false)
    const [item, setItem] = useState()


    const getListPending = async () => {
        const res = await getByStatus([3])

        const list = res.data.data
        if (list) {
            const newList = list.map((item, index) => {
                return {
                    ...item,
                    key: index
                }
            })

            setListPending(newList)
        } else {
            setListPending([])
        }
    }

    const openPendingDialog = (rowData) => {
        setItem(rowData)
        setShouldOpenPendingDialog(true)
    }

    const onCancelPendingDialog = () => {
        setItem({})
        setShouldOpenPendingDialog(false)
        getListPending()
    }

    const columns = Columns(openPendingDialog)

    useEffect(() => {
        getListPending()
    }, [])

    return (
        <div className="m-sm-30">
            <Breadcrumb style={{ padding: '0 0 30px 0' }}>
                <Breadcrumb.Item href="/">
                    <HomeFilled />
                </Breadcrumb.Item>
                <Breadcrumb.Item href="/conclude/manage">
                    <span>Quản lý nhân viên</span>
                </Breadcrumb.Item>
                <Breadcrumb.Item>Tạo mới</Breadcrumb.Item>
            </Breadcrumb>

            <Row justify="end" style={{ marginBottom: 20 }}>
                <Input.Search style={{ width: 300 }} placeholder="Search"></Input.Search>
            </Row>

            <div>
                {shouldOpenPendingDialog && (
                    <PendingDialog
                        open={shouldOpenPendingDialog}
                        item={item}
                        // onOk={onOk}
                        onCancel={onCancelPendingDialog}
                    />
                )}
            </div>

            <HeaderStyled>
                <Table
                    columns={columns}
                    dataSource={listPending}
                    pagination={{ defaultPageSize: 10 }}
                    scroll={{ y: 350, }}
                />
            </HeaderStyled>

        </div>
    )
}

export default Pending
