import React, { useEffect } from "react";
import { Modal } from 'antd';
import { Tabs } from 'antd';
import StaffProfile from "./staff-profile";
import DiplomaInfo from "./diploma-info";
import CurriculumVitae from "./curriculum-vitae";
import { getFormStaff, getStaffById } from "../CreateStaffService";
import { useState } from "react";
import styled from "styled-components";

const TabPane = Tabs.TabPane

const DialogStaffInfo = ({ open, handleClose, employeeId }) => {
    const [employeeInfo, setEmployeeInfo] = useState({})
    const [employeeResume, setEmployeeResume] = useState({})
    const [employeeCv, setEmployeeCv] = useState({})

    const onChange = (key) => { };

    const handleGetForm = async (employeeId) => {
        const res = await getFormStaff({ employeeId })
        console.log(res)
        if (res?.data?.code === 200) {
            setEmployeeResume(res?.data?.data?.resume)
            setEmployeeCv(res?.data?.data?.cv)
        }

    }

    console.log(employeeCv)

    useEffect(() => {
        handleGetForm(employeeId)
    }, [])

    const handleGetStaffById = async (employeeId) => {
        const res = await getStaffById({ employeeId })

        if (res?.data?.code === 200) {
            setEmployeeInfo(res?.data?.data)
        }

    }

    useEffect(() => {
        if (employeeId) {
            handleGetStaffById(employeeId)
        }
    }, [])

    return (
        <StyledModal
            title="Hồ sơ nhân viên"
            centered
            open={open}
            width={1000}
            onCancel={handleClose}
            footer={null}
        >
            <Tabs defaultActiveKey="1" tabPosition="left" onChange={onChange}>
                <TabPane tab="HỒ SƠ" key="1">
                    <StaffProfile
                        employeeInfo={employeeInfo}
                        employeeCv={employeeCv}

                    />
                </TabPane>
                <TabPane tab="SƠ YẾU LÝ LỊCH" key="2">
                    <CurriculumVitae
                        employeeInfo={employeeInfo}
                        employeeResume={employeeResume}
                    />
                </TabPane>
                <TabPane tab="DANH SÁCH VĂN BẰNG" key="3">
                    <DiplomaInfo
                        employeeInfo={employeeInfo}
                    />
                </TabPane>
            </Tabs>
        </StyledModal>
    );
}


export default DialogStaffInfo;

const StyledModal = styled(Modal)`
    .ant-modal {
        height: 90vh;
    }
    .ant-modal-body {
        padding: 20px 0;
        height: 82vh;
        overflow-y: hidden;
    }
    .ant-modal-content {
        height: 90vh;
        overflow: hidden;
    }
    
`
