import { Button, Modal, Tabs } from 'antd';
import React from 'react';
import { useState } from 'react';
import SalaryIncreaseForm from './SalaryIncreaseForm';
import CurriculumVitae from 'app/views/general-settings/CurriculumVitae';

function SalaryIncreaseModal({ open, onCancel, staffInfo, salaryRecord, onSend }) {
    const [dataForm, setDataForm] = useState({
        fullName: staffInfo?.fullName || '',
    });

    const onChangeValue = (e) => {
        setDataForm({ ...dataForm, [e.target.name]: e.target.defaultValue });
    };

    return (
        <Modal
            className="information_modal modal_profile"
            centered
            title="Biểu mẫu"
            open={open}
            onCancel={onCancel}
            width={'70vw'}
            footer={[
                <Button onClick={onCancel} key="1">
                    Hủy
                </Button>,
                <Button type="primary" onClick={onSend} key="2">
                    Trình lãnh đạo
                </Button>,
                <Button className="btn-save" key="3">
                    Lưu
                </Button>,
            ]}
        >
            <Tabs
                defaultActiveKey="1"
                tabPosition="left"
                tabBarStyle={{ width: 180, whiteSpace: 'break-spaces' }}
                items={[
                    {
                        label: 'Sơ yếu lí lịch',
                        key: 1,
                        children: <CurriculumVitae staffInfo={staffInfo} />,
                    },
                    {
                        label: 'Biểu mẫu tăng lương',
                        key: 2,
                        children: (
                            <SalaryIncreaseForm
                                onChangeValue={onChangeValue}
                                staffInfo={staffInfo}
                                salaryRecord={salaryRecord}
                            />
                        ),
                    },
                ]}
            />
        </Modal>
    );
}

export default SalaryIncreaseModal;
