import { Button, Col, Form, Input, Modal, Row } from "antd";
import { sendLeaderStaff } from "app/views/create-staff/CreateStaffService";
import React from "react";
import styled from "styled-components";
import Styled from "./styled";
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

toast.configure({
    autoClose: 1000,
    draggable: false,
    limit: 3
});

const DialogSendLeader = ({ open, employeeId, handleClose, handleClosePopupSendLeader, handleCloseDialogRegister }) => {
    const [form] = Form.useForm();

    const handleSubmitRegister = async (value) => {
        const res = await sendLeaderStaff({ employeeId, value })
        if (res?.data?.code === 200) {
            if (res?.data?.code === 200) {
                toast.success("Trình lãnh đạo thành công")
                handleClose()
                handleCloseDialogRegister()
            } else {
                toast.warning('Chỉ trình được với trạng thái là Lưu mới')
            }
        }
    }

    return (
        <StyledModal
            title="Trình lãnh đạo"
            centered
            open={open}
            width={700}
            onCancel={handleClosePopupSendLeader}
            footer={null}
        >
            <Styled>
                <Form
                    form={form}
                    onFinish={handleSubmitRegister}
                    layout="vertical"
                >
                    <Row justify="space-between">
                        <Col span={6}>
                            <Form.Item
                                label='Ngày trình'
                                name="registerDate"
                                rules={[
                                    {
                                        required: true,
                                        message: 'Bắt buộc phải nhập trường này!',
                                    },
                                ]}
                            >
                                <Input type="date" style={{ width: "100%" }} />
                            </Form.Item>
                        </Col>
                        <Col span={10}>
                            <Form.Item
                                label='Tên lãnh đạo'
                                name="registerName"
                                rules={[
                                    {
                                        required: true,
                                        message: 'Bắt buộc phải nhập trường này!',
                                    },
                                ]}
                            >
                                <Input style={{ width: "100%" }} />
                            </Form.Item>
                        </Col>
                        <Col span={8}>
                            <Form.Item
                                label='Chức vụ'
                                name="registerPosition"
                                rules={[
                                    {
                                        required: true,
                                        message: 'Bắt buộc phải nhập trường này!',
                                    },
                                ]}
                            >
                                <Input style={{ width: "100%" }} />
                            </Form.Item>
                        </Col>
                        <Col span={24}>
                            <Form.Item
                                label='Nội dung trình lãnh đạo'
                                name="registerContent"
                                rules={[
                                    {
                                        required: true,
                                        message: 'Bắt buộc phải nhập trường này!',
                                    },
                                ]}
                            >
                                <Input style={{ width: "100%" }} />
                            </Form.Item>
                        </Col>
                    </Row>
                    <div className="btn-end-position">
                        <Button className="btn-register" htmlType="submit">TRÌNH LÃNH ĐẠO</Button>
                        <Button className="btn-close" onClick={handleClosePopupSendLeader} >HỦY</Button>
                    </div>
                </Form>
            </Styled>
        </StyledModal>
    )
}

export default DialogSendLeader;

const StyledModal = styled(Modal)`
    .ant-modal-body{
        padding: 20px;
    }

    .btn-end-position{
        margin-top: 15px;
    }

    .btn-close {
        background-color: orange;
        color: #fff;
    }
    .btn-close:hover {
        background-color: #ffb833;
        color: #fff;
    }
    .btn-register {
        background-color: #0d5ac3;
        color: #fff;
    }
    .btn-register:hover {
        background-color: #3365a9;
        color: #fff;
    }
    
`
